INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('11ef85c8-a6cd-4f62-911d-caa0f095566c', 1, '1.1', 'Eradicate extreme poverty', 'By 2030, eradicate extreme poverty for all people everywhere, currently measured as people living on less than $1.25 a day', '2025-08-01 08:46:26.19073+00', '2025-08-01 08:46:26.19073+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('4ef9736d-8f1e-4767-994b-a1608406885b', 1, '1.2', 'Reduce poverty by half', 'By 2030, reduce at least by half the proportion of men, women and children of all ages living in poverty in all its dimensions according to national definitions', '2025-08-01 08:46:26.226246+00', '2025-08-01 08:46:26.226246+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('9d69a1c5-7a2c-4412-9200-0d66a5040bd8', 1, '1.3', 'Social protection systems', 'Implement nationally appropriate social protection systems and measures for all, including floors, and by 2030 achieve substantial coverage of the poor and the vulnerable', '2025-08-01 08:46:26.259939+00', '2025-08-01 08:46:26.259939+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('5513de9f-5459-4734-bac7-a2d04a4631b8', 1, '1.4', 'Equal access to basic services', 'By 2030, ensure that all men and women, in particular the poor and the vulnerable, have equal rights to economic resources, as well as access to basic services, ownership and control over land and other forms of property, inheritance, natural resources, appropriate new technology and financial services, including microfinance', '2025-08-01 08:46:26.29291+00', '2025-08-01 08:46:26.29291+00'),
('2821304e-e6ec-4c2c-b9f3-505311b57526', 1, '1.5', 'Resilience to disasters', 'By 2030, build the resilience of the poor and those in vulnerable situations and reduce their exposure and vulnerability to climate-related extreme events and other economic, social and environmental shocks and disasters', '2025-08-01 08:46:26.326787+00', '2025-08-01 08:46:26.326787+00'),
('6d5c8031-7374-449e-a448-b706e46f4a2d', 1, '1.a', 'Resource mobilization', 'Ensure significant mobilization of resources from a variety of sources, including through enhanced development cooperation, in order to provide adequate and predictable means for developing countries, in particular least developed countries, to implement programmes and policies to end poverty in all its dimensions', '2025-08-01 08:46:26.359913+00', '2025-08-01 08:46:26.359913+00'),
('08097f0c-e48e-43b3-8915-d50d14d9811b', 1, '1.b', 'Policy frameworks', 'Create sound policy frameworks at the national, regional and international levels, based on pro-poor and gender-sensitive development strategies, to support accelerated investment in poverty eradication actions', '2025-08-01 08:46:26.392964+00', '2025-08-01 08:46:26.392964+00'),
('ab3b8382-0d30-4b2e-b183-249aa3dc1b15', 2, '2.1', 'End hunger', 'By 2030, end hunger and ensure access by all people, in particular the poor and people in vulnerable situations, including infants, to safe, nutritious and sufficient food all year round', '2025-08-01 08:46:26.425768+00', '2025-08-01 08:46:26.425768+00'),
('322f984d-9b98-4491-9199-2585ae5e56fc', 2, '2.2', 'End malnutrition', 'By 2030, end all forms of malnutrition, including achieving, by 2025, the internationally agreed targets on stunting and wasting in children under 5 years of age, and address the nutritional needs of adolescent girls, pregnant and lactating women and older persons', '2025-08-01 08:46:26.459075+00', '2025-08-01 08:46:26.459075+00'),
('b3cf8e55-40e6-4f26-89ec-005410e23283', 2, '2.3', 'Double agricultural productivity', 'By 2030, double the agricultural productivity and incomes of small-scale food producers, in particular women, indigenous peoples, family farmers, pastoralists and fishers, including through secure and equal access to land, other productive resources and inputs, knowledge, financial services, markets and opportunities for value addition and non-farm employment', '2025-08-01 08:46:26.492237+00', '2025-08-01 08:46:26.492237+00'),
('a7eca4ce-d8cf-4212-bc7b-c27b816d233f', 2, '2.4', 'Sustainable food production', 'By 2030, ensure sustainable food production systems and implement resilient agricultural practices that increase productivity and production, that help maintain ecosystems, that strengthen capacity for adaptation to climate change, extreme weather, drought, flooding and other disasters and that progressively improve land and soil quality', '2025-08-01 08:46:26.525914+00', '2025-08-01 08:46:26.525914+00'),
('229de58b-9c03-4795-90b1-fba2092b1e15', 2, '2.5', 'Maintain genetic diversity', 'By 2020, maintain the genetic diversity of seeds, cultivated plants and farmed and domesticated animals and their related wild species, including through soundly managed and diversified seed and plant banks at the national, regional and international levels, and promote access to and fair and equitable sharing of benefits arising from the utilization of genetic resources and associated traditional knowledge, as internationally agreed', '2025-08-01 08:46:26.55984+00', '2025-08-01 08:46:26.55984+00'),
('e15e1611-4b4f-431f-9234-e9c407b01f0e', 2, '2.a', 'Increase agricultural investment', 'Increase investment, including through enhanced international cooperation, in rural infrastructure, agricultural research and extension services, technology development and plant and livestock gene banks in order to enhance agricultural productive capacity in developing countries, in particular least developed countries', '2025-08-01 08:46:26.59678+00', '2025-08-01 08:46:26.59678+00'),
('37976cc6-b7fd-4b37-89eb-24f756c3e6ec', 5, '5.4', 'Value unpaid care work', 'Recognize and value unpaid care and domestic work through the provision of public services, infrastructure and social protection policies and the promotion of shared responsibility within the household and the family as nationally appropriate', '2025-08-01 08:46:27.56151+00', '2025-08-01 08:46:27.56151+00'),
('431e440e-3ee4-4081-83ec-a5e4a5e11a5e', 2, '2.b', 'Correct trade restrictions', 'Correct and prevent trade restrictions and distortions in world agricultural markets, including through the parallel elimination of all forms of agricultural export subsidies and all export measures with equivalent effect, in accordance with the mandate of the Doha Development Round', '2025-08-01 08:46:26.62988+00', '2025-08-01 08:46:26.62988+00'),
('9668e01d-2087-4ee6-862f-d01e40a8dc17', 2, '2.c', 'Food commodity markets', 'Adopt measures to ensure the proper functioning of food commodity markets and their derivatives and facilitate timely access to market information, including on food reserves, in order to help limit extreme food price volatility', '2025-08-01 08:46:26.66645+00', '2025-08-01 08:46:26.66645+00'),
('ee93f689-a889-4c48-9a19-34abc11919d2', 3, '3.1', 'Reduce maternal mortality', 'By 2030, reduce the global maternal mortality ratio to less than 70 per 100,000 live births', '2025-08-01 08:46:26.699753+00', '2025-08-01 08:46:26.699753+00'),
('8cc8d0aa-0bb2-4f6c-9daa-c2b322a78938', 3, '3.2', 'End preventable deaths of children', 'By 2030, end preventable deaths of newborns and children under 5 years of age, with all countries aiming to reduce neonatal mortality to at least as low as 12 per 1,000 live births and under5 mortality to at least as low as 25 per 1,000 live births', '2025-08-01 08:46:26.732924+00', '2025-08-01 08:46:26.732924+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('a19a2f29-37df-46bb-b68a-7d5174a2f0f7', 3, '3.3', 'End epidemics', 'By 2030, end the epidemics of AIDS, tuberculosis, malaria and neglected tropical diseases and combat hepatitis, water-borne diseases and other communicable diseases', '2025-08-01 08:46:26.766041+00', '2025-08-01 08:46:26.766041+00'),
('e9a1ef6b-f51a-4674-b613-b2ac9cbbebc5', 3, '3.4', 'Reduce non-communicable diseases', 'By 2030, reduce by one third premature mortality from non-communicable diseases through prevention and treatment and promote mental health and well-being', '2025-08-01 08:46:26.799191+00', '2025-08-01 08:46:26.799191+00'),
('eb3156b9-fb1b-4755-8c5b-8d17276cd701', 3, '3.5', 'Strengthen substance abuse prevention', 'Strengthen the prevention and treatment of substance abuse, including narcotic drug abuse and harmful use of alcohol', '2025-08-01 08:46:26.833049+00', '2025-08-01 08:46:26.833049+00'),
('c3b2465d-347f-4b0f-8b7e-81e71e5ff001', 3, '3.6', 'Halve road traffic deaths', 'By 2020, halve the number of global deaths and injuries from road traffic accidents', '2025-08-01 08:46:26.865784+00', '2025-08-01 08:46:26.865784+00'),
('5683c097-0554-4d77-851e-08d8722ed1cb', 3, '3.7', 'Universal reproductive health', 'By 2030, ensure universal access to sexual and reproductive health-care services, including for family planning, information and education, and the integration of reproductive health into national strategies and programmes', '2025-08-01 08:46:26.899305+00', '2025-08-01 08:46:26.899305+00'),
('22c2c8c4-2fc7-4939-a5e0-381e46dbea7e', 3, '3.8', 'Universal health coverage', 'Achieve universal health coverage, including financial risk protection, access to quality essential health-care services and access to safe, effective, quality and affordable essential medicines and vaccines for all', '2025-08-01 08:46:26.932925+00', '2025-08-01 08:46:26.932925+00'),
('7ebd35cc-040a-4c84-971c-d24ef3784e6d', 3, '3.9', 'Reduce pollution deaths', 'By 2030, substantially reduce the number of deaths and illnesses from hazardous chemicals and air, water and soil pollution and contamination', '2025-08-01 08:46:26.966262+00', '2025-08-01 08:46:26.966262+00'),
('6e3180e2-6dcb-4cca-9bc6-d9e9c33c2f6a', 3, '3.a', 'Tobacco control', 'Strengthen the implementation of the World Health Organization Framework Convention on Tobacco Control in all countries, as appropriate', '2025-08-01 08:46:26.999289+00', '2025-08-01 08:46:26.999289+00'),
('ddb202a0-1502-4740-b472-97b738cbb77a', 3, '3.b', 'Support vaccine research', 'Support the research and development of vaccines and medicines for the communicable and noncommunicable diseases that primarily affect developing countries, provide access to affordable essential medicines and vaccines, in accordance with the Doha Declaration on the TRIPS Agreement and Public Health', '2025-08-01 08:46:27.03239+00', '2025-08-01 08:46:27.03239+00'),
('abe91656-fc1f-4ac2-bb48-8e6ba582b1a7', 3, '3.c', 'Increase health financing', 'Substantially increase health financing and the recruitment, development, training and retention of the health workforce in developing countries, especially in least developed countries and small island developing States', '2025-08-01 08:46:27.065805+00', '2025-08-01 08:46:27.065805+00'),
('fa395cce-6530-49f9-bf3c-519dfbb2ec93', 3, '3.d', 'Strengthen health risk management', 'Strengthen the capacity of all countries, in particular developing countries, for early warning, risk reduction and management of national and global health risks', '2025-08-01 08:46:27.098643+00', '2025-08-01 08:46:27.098643+00'),
('c3a01e02-d592-43ce-a675-45ee0e6c8d37', 4, '4.1', 'Free primary and secondary education', 'By 2030, ensure that all girls and boys complete free, equitable and quality primary and secondary education leading to relevant and effective learning outcomes', '2025-08-01 08:46:27.131761+00', '2025-08-01 08:46:27.131761+00'),
('2552b1fc-634c-4728-8649-72ec3417df4b', 4, '4.2', 'Early childhood development', 'By 2030, ensure that all girls and boys have access to quality early childhood development, care and preprimary education so that they are ready for primary education', '2025-08-01 08:46:27.164759+00', '2025-08-01 08:46:27.164759+00'),
('66cf4c02-489c-4870-9416-db4b4b297d9a', 4, '4.3', 'Equal access to technical/vocational education', 'By 2030, ensure equal access for all women and men to affordable and quality technical, vocational and tertiary education, including university', '2025-08-01 08:46:27.19778+00', '2025-08-01 08:46:27.19778+00'),
('2b02e19e-da10-4c34-a0ed-b8371efd2b39', 4, '4.4', 'Increase relevant skills for employment', 'By 2030, substantially increase the number of youth and adults who have relevant skills, including technical and vocational skills, for employment, decent jobs and entrepreneurship', '2025-08-01 08:46:27.230826+00', '2025-08-01 08:46:27.230826+00'),
('e5b56541-96a1-45ca-8d4b-9608320d0861', 4, '4.5', 'Eliminate gender disparities', 'By 2030, eliminate gender disparities in education and ensure equal access to all levels of education and vocational training for the vulnerable, including persons with disabilities, indigenous peoples and children in vulnerable situations', '2025-08-01 08:46:27.263892+00', '2025-08-01 08:46:27.263892+00'),
('2fbb31e8-ced3-40c7-b637-2855538e117c', 4, '4.6', 'Adult literacy and numeracy', 'By 2030, ensure that all youth and a substantial proportion of adults, both men and women, achieve literacy and numeracy', '2025-08-01 08:46:27.297225+00', '2025-08-01 08:46:27.297225+00'),
('a0b4adbc-b220-437e-8691-1f2894ebcd00', 4, '4.7', 'Education for sustainable development', 'By 2030, ensure that all learners acquire the knowledge and skills needed to promote sustainable development, including, among others, through education for sustainable development and sustainable lifestyles, human rights, gender equality, promotion of a culture of peace and non-violence, global citizenship and appreciation of cultural diversity', '2025-08-01 08:46:27.331307+00', '2025-08-01 08:46:27.331307+00'),
('c7befe2c-812f-4353-9093-0cac2c2b948f', 4, '4.a', 'Build and upgrade education facilities', 'Build and upgrade education facilities that are child, disability and gender sensitive and provide safe, non-violent, inclusive and effective learning environments for all', '2025-08-01 08:46:27.364746+00', '2025-08-01 08:46:27.364746+00'),
('7b70a61f-8775-463b-9d6a-07160aa904c6', 4, '4.b', 'Expand higher education scholarships', 'By 2020, substantially expand globally the number of scholarships available to developing countries, in particular least developed countries, small island developing States and African countries, for enrolment in higher education', '2025-08-01 08:46:27.398095+00', '2025-08-01 08:46:27.398095+00'),
('e1b91830-b362-44cf-9d69-10b63012824d', 4, '4.c', 'Increase supply of qualified teachers', 'By 2030, substantially increase the supply of qualified teachers, including through international cooperation for teacher training in developing countries, especially least developed countries and small island developing States', '2025-08-01 08:46:27.431548+00', '2025-08-01 08:46:27.431548+00'),
('54883b63-6886-417e-8cd4-4e5b5476f597', 5, '5.1', 'End discrimination against women', 'End all forms of discrimination against all women and girls everywhere', '2025-08-01 08:46:27.463351+00', '2025-08-01 08:46:27.463351+00'),
('6c05f1d8-1491-4128-b177-f3973483d7ae', 5, '5.2', 'Eliminate violence against women', 'Eliminate all forms of violence against all women and girls in the public and private spheres, including trafficking and sexual and other types of exploitation', '2025-08-01 08:46:27.496196+00', '2025-08-01 08:46:27.496196+00'),
('c7695550-a14a-4d43-8b07-d49455054f24', 5, '5.3', 'Eliminate harmful practices', 'Eliminate all harmful practices, such as child, early and forced marriage and female genital mutilation', '2025-08-01 08:46:27.529362+00', '2025-08-01 08:46:27.529362+00'),
('3608f2bb-906e-4615-a817-cc7abbfb3214', 5, '5.5', 'Ensure full participation in leadership', 'Ensure women''s full and effective participation and equal opportunities for leadership at all levels of decision-making in political, economic and public life', '2025-08-01 08:46:27.594316+00', '2025-08-01 08:46:27.594316+00'),
('68ac76e2-b2a3-4fd3-b690-5db9f0914e24', 5, '5.6', 'Universal access to reproductive rights', 'Ensure universal access to sexual and reproductive health and reproductive rights as agreed in accordance with the Programme of Action of the International Conference on Population and Development and the Beijing Platform for Action', '2025-08-01 08:46:27.627495+00', '2025-08-01 08:46:27.627495+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('94e99071-20dd-4d75-969a-0d0a162d937f', 5, '5.a', 'Equal rights to economic resources', 'Undertake reforms to give women equal rights to economic resources, as well as access to ownership and control over land and other forms of property, financial services, inheritance and natural resources, in accordance with national laws', '2025-08-01 08:46:27.660591+00', '2025-08-01 08:46:27.660591+00'),
('dc64b41e-10dd-4f3c-ac70-74862231c1e0', 5, '5.b', 'Enhance use of enabling technology', 'Enhance the use of enabling technology, in particular information and communications technology, to promote the empowerment of women', '2025-08-01 08:46:27.692455+00', '2025-08-01 08:46:27.692455+00'),
('3197ac4b-fdbb-4524-9a39-99dfbabdcde3', 5, '5.c', 'Adopt policies for gender equality', 'Adopt and strengthen sound policies and enforceable legislation for the promotion of gender equality and the empowerment of all women and girls at all levels', '2025-08-01 08:46:27.725462+00', '2025-08-01 08:46:27.725462+00'),
('09e8a727-1d90-43c2-8e91-fa54e6921793', 6, '6.1', 'Safe and affordable drinking water', 'By 2030, achieve universal and equitable access to safe and affordable drinking water for all', '2025-08-01 08:46:27.758354+00', '2025-08-01 08:46:27.758354+00'),
('3137bcb8-5506-4451-8ba5-673d51c8ccf3', 6, '6.2', 'Adequate sanitation and hygiene', 'By 2030, achieve access to adequate and equitable sanitation and hygiene for all and end open defecation, paying special attention to the needs of women and girls and those in vulnerable situations', '2025-08-01 08:46:27.791353+00', '2025-08-01 08:46:27.791353+00'),
('15743eb6-593f-485c-963d-454098c75ac6', 6, '6.3', 'Improve water quality', 'By 2030, improve water quality by reducing pollution, eliminating dumping and minimizing release of hazardous chemicals and materials, halving the proportion of untreated wastewater and substantially increasing recycling and safe reuse globally', '2025-08-01 08:46:27.824195+00', '2025-08-01 08:46:27.824195+00'),
('8db49864-18d2-4c07-9baf-3101a0e3bb43', 6, '6.4', 'Increase water-use efficiency', 'By 2030, substantially increase water-use efficiency across all sectors and ensure sustainable withdrawals and supply of freshwater to address water scarcity and substantially reduce the number of people suffering from water scarcity', '2025-08-01 08:46:27.856915+00', '2025-08-01 08:46:27.856915+00'),
('76346cb0-64d4-4197-a2a2-ad88482953ec', 6, '6.5', 'Integrated water resources management', 'By 2030, implement integrated water resources management at all levels, including through transboundary cooperation as appropriate', '2025-08-01 08:46:27.889862+00', '2025-08-01 08:46:27.889862+00'),
('40f5bff8-d1ba-4c85-8ad4-b88f6b8acf7a', 6, '6.6', 'Protect water-related ecosystems', 'By 2020, protect and restore water-related ecosystems, including mountains, forests, wetlands, rivers, aquifers and lakes', '2025-08-01 08:46:27.922863+00', '2025-08-01 08:46:27.922863+00'),
('0fed54f6-3244-4230-adb0-b58d8b959402', 6, '6.a', 'Expand water cooperation', 'By 2030, expand international cooperation and capacity-building support to developing countries in water- and sanitation-related activities and programmes, including water harvesting, desalination, water efficiency, wastewater treatment, recycling and reuse technologies', '2025-08-01 08:46:27.955997+00', '2025-08-01 08:46:27.955997+00'),
('d8f8f39b-0872-4f69-9c95-f65cfc27d7e3', 6, '6.b', 'Support local participation', 'Support and strengthen the participation of local communities in improving water and sanitation management', '2025-08-01 08:46:27.988818+00', '2025-08-01 08:46:27.988818+00'),
('42a06005-6d1a-45bb-b470-794073c69507', 7, '7.1', 'Universal access to modern energy', 'By 2030, ensure universal access to affordable, reliable and modern energy services', '2025-08-01 08:46:28.021744+00', '2025-08-01 08:46:28.021744+00'),
('3e97179e-157d-445d-9d74-d56a96b03d3a', 7, '7.2', 'Increase renewable energy', 'By 2030, increase substantially the share of renewable energy in the global energy mix', '2025-08-01 08:46:28.055122+00', '2025-08-01 08:46:28.055122+00'),
('5885e418-7a69-473e-99df-02f3a026eb57', 7, '7.3', 'Double energy efficiency', 'By 2030, double the global rate of improvement in energy efficiency', '2025-08-01 08:46:28.088024+00', '2025-08-01 08:46:28.088024+00'),
('90d68cad-8138-45d7-8c00-cf0d508661e8', 7, '7.a', 'Enhance energy cooperation', 'By 2030, enhance international cooperation to facilitate access to clean energy research and technology, including renewable energy, energy efficiency and advanced and cleaner fossil-fuel technology, and promote investment in energy infrastructure and clean energy technology', '2025-08-01 08:46:28.120882+00', '2025-08-01 08:46:28.120882+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('2c4baa29-3af8-4ea1-ad4f-787b9c85d3bc', 7, '7.b', 'Expand energy infrastructure', 'By 2030, expand infrastructure and upgrade technology for supplying modern and sustainable energy services for all in developing countries, in particular least developed countries, small island developing States, and land-locked developing countries, in accordance with their respective programmes of support', '2025-08-01 08:46:28.153862+00', '2025-08-01 08:46:28.153862+00'),
('775b600a-3e14-4db6-ad7b-53def37c35a0', 8, '8.1', 'Sustain economic growth', 'Sustain per capita economic growth in accordance with national circumstances and, in particular, at least 7 per cent gross domestic product growth per annum in the least developed countries', '2025-08-01 08:46:28.186866+00', '2025-08-01 08:46:28.186866+00'),
('1460431f-9261-47bd-96dc-e49f4821bfe8', 8, '8.2', 'Economic productivity through diversification', 'Achieve higher levels of economic productivity through diversification, technological upgrading and innovation, including through a focus on high-value added and labour-intensive sectors', '2025-08-01 08:46:28.219811+00', '2025-08-01 08:46:28.219811+00'),
('38698272-c2ba-4e22-82a4-40f31ed43677', 8, '8.3', 'Promote development-oriented policies', 'Promote development-oriented policies that support productive activities, decent job creation, entrepreneurship, creativity and innovation, and encourage the formalization and growth of micro-, small- and medium-sized enterprises, including through access to financial services', '2025-08-01 08:46:28.253029+00', '2025-08-01 08:46:28.253029+00'),
('1db9cd65-872a-4ddc-a373-61c6bd4b42ff', 8, '8.4', 'Improve resource efficiency', 'Improve progressively, through 2030, global resource efficiency in consumption and production and endeavour to decouple economic growth from environmental degradation, in accordance with the 10-year framework of programmes on sustainable consumption and production, with developed countries taking the lead', '2025-08-01 08:46:28.285694+00', '2025-08-01 08:46:28.285694+00'),
('82b0cf22-09b6-4e7f-ab40-c56a79be15a1', 8, '8.5', 'Full employment and decent work', 'By 2030, achieve full and productive employment and decent work for all women and men, including for young people and persons with disabilities, and equal pay for work of equal value', '2025-08-01 08:46:28.317462+00', '2025-08-01 08:46:28.317462+00'),
('abdc5591-78ac-4f30-a8f8-240a8591e452', 8, '8.6', 'Reduce youth NEET', 'By 2020, substantially reduce the proportion of youth not in employment, education or training', '2025-08-01 08:46:28.350324+00', '2025-08-01 08:46:28.350324+00'),
('e2cc0942-eb36-46e2-a33e-0df9713d9464', 8, '8.7', 'Eradicate forced labour', 'Take immediate and effective measures to eradicate forced labour, end modern slavery and human trafficking and secure the prohibition and elimination of the worst forms of child labour, including recruitment and use of child soldiers, and by 2025 end child labour in all its forms', '2025-08-01 08:46:28.38437+00', '2025-08-01 08:46:28.38437+00'),
('26de03e6-a43f-41aa-9934-74b3942c7cd0', 8, '8.8', 'Protect labour rights', 'Protect labour rights and promote safe and secure working environments for all workers, including migrant workers, in particular women migrants, and those in precarious employment', '2025-08-01 08:46:28.417411+00', '2025-08-01 08:46:28.417411+00'),
('d29684ff-0494-4552-990c-8933d1955fd8', 8, '8.9', 'Promote sustainable tourism', 'By 2030, devise and implement policies to promote sustainable tourism that creates jobs and promotes local culture and products', '2025-08-01 08:46:28.45015+00', '2025-08-01 08:46:28.45015+00'),
('06cd00fa-1e54-4785-a954-1d8e205a0980', 8, '8.10', 'Strengthen financial institutions', 'Strengthen the capacity of domestic financial institutions to encourage and expand access to banking, insurance and financial services for all', '2025-08-01 08:46:28.482886+00', '2025-08-01 08:46:28.482886+00'),
('13758bdb-6844-431c-9e2e-4bb3da727b65', 8, '8.a', 'Increase Aid for Trade', 'Increase Aid for Trade support for developing countries, in particular least developed countries, including through the Enhanced Integrated Framework for Trade-Related Technical Assistance to Least Developed Countries', '2025-08-01 08:46:28.515677+00', '2025-08-01 08:46:28.515677+00'),
('64f2c77e-13ac-4605-93d6-8902a434ea82', 8, '8.b', 'Youth employment strategy', 'By 2020, develop and operationalize a global strategy for youth employment and implement the Global Jobs Pact of the International Labour Organization', '2025-08-01 08:46:28.548579+00', '2025-08-01 08:46:28.548579+00'),
('3b91369c-2885-4461-be1a-a9d69e8d9ed5', 9, '9.1', 'Develop resilient infrastructure', 'Develop quality, reliable, sustainable and resilient infrastructure, including regional and transborder infrastructure, to support economic development and human well-being, with a focus on affordable and equitable access for all', '2025-08-01 08:46:28.581339+00', '2025-08-01 08:46:28.581339+00'),
('be5f6c61-c48d-42d8-9253-b5840787aeab', 9, '9.2', 'Promote inclusive industrialization', 'Promote inclusive and sustainable industrialization and, by 2030, significantly raise industry''s share of employment and gross domestic product, in line with national circumstances, and double its share in least developed countries', '2025-08-01 08:46:28.614079+00', '2025-08-01 08:46:28.614079+00'),
('c286b3e8-dbd1-4722-8b10-deaa286125cc', 9, '9.3', 'Increase access to financial services', 'Increase the access of small-scale industrial and other enterprises, in particular in developing countries, to financial services, including affordable credit, and their integration into value chains and markets', '2025-08-01 08:46:28.647175+00', '2025-08-01 08:46:28.647175+00'),
('f2854787-8160-4dad-a1e8-0e7aefc4d176', 9, '9.4', 'Upgrade infrastructure for sustainability', 'By 2030, upgrade infrastructure and retrofit industries to make them sustainable, with increased resource-use efficiency and greater adoption of clean and environmentally sound technologies and industrial processes, with all countries taking action in accordance with their respective capabilities', '2025-08-01 08:46:28.680792+00', '2025-08-01 08:46:28.680792+00'),
('6692ef49-2de2-4721-97ee-b063c7de518a', 9, '9.5', 'Enhance scientific research', 'Enhance scientific research, upgrade the technological capabilities of industrial sectors in all countries, in particular developing countries, including, by 2030, encouraging innovation and substantially increasing the number of research and development workers per 1 million people and public and private research and development spending', '2025-08-01 08:46:28.713558+00', '2025-08-01 08:46:28.713558+00'),
('b3fa9122-b29d-465d-92f7-10c9a62c0816', 9, '9.a', 'Facilitate sustainable infrastructure', 'Facilitate sustainable and resilient infrastructure development in developing countries through enhanced financial, technological and technical support to African countries, least developed countries, landlocked developing countries and small island developing States', '2025-08-01 08:46:28.746798+00', '2025-08-01 08:46:28.746798+00'),
('cc1d21a1-805a-4da0-89ac-000780bf6d39', 9, '9.b', 'Support technology development', 'Support domestic technology development, research and innovation in developing countries, including by ensuring a conducive policy environment for, inter alia, industrial diversification and value addition to commodities', '2025-08-01 08:46:28.779442+00', '2025-08-01 08:46:28.779442+00'),
('f72f8f29-e3d7-4a1e-a06b-324f17b61544', 9, '9.c', 'Increase access to ICT', 'Significantly increase access to information and communications technology and strive to provide universal and affordable access to the Internet in least developed countries by 2020', '2025-08-01 08:46:28.812111+00', '2025-08-01 08:46:28.812111+00'),
('0d98194c-ffc9-4f91-9d12-b21e1bee3b1a', 10, '10.1', 'Reduce income inequalities', 'By 2030, progressively achieve and sustain income growth of the bottom 40 per cent of the population at a rate higher than the national average', '2025-08-01 08:46:28.845188+00', '2025-08-01 08:46:28.845188+00'),
('1f211ada-f74d-4776-ae0c-893ca1e46cb7', 10, '10.2', 'Promote universal social inclusion', 'By 2030, empower and promote the social, economic and political inclusion of all, irrespective of age, sex, disability, race, ethnicity, origin, religion or economic or other status', '2025-08-01 08:46:28.878665+00', '2025-08-01 08:46:28.878665+00'),
('28e9b5a8-dea3-493c-9d92-80a585bde86e', 10, '10.3', 'Ensure equal opportunity', 'Ensure equal opportunity and reduce inequalities of outcome, including by eliminating discriminatory laws, policies and practices and promoting appropriate legislation, policies and action in this regard', '2025-08-01 08:46:28.911642+00', '2025-08-01 08:46:28.911642+00'),
('c7552b46-1861-4dc3-9beb-0a47d26c6954', 10, '10.4', 'Adopt fiscal and social policies', 'Adopt policies, especially fiscal, wage and social protection policies, and progressively achieve greater equality', '2025-08-01 08:46:28.944835+00', '2025-08-01 08:46:28.944835+00'),
('ccddd9e0-86d4-476f-8810-c3f8ae774aea', 10, '10.5', 'Improve regulation of financial markets', 'Improve the regulation and monitoring of global financial markets and institutions and strengthen the implementation of such regulations', '2025-08-01 08:46:28.9778+00', '2025-08-01 08:46:28.9778+00'),
('99a19dba-6fe8-4a88-8ee9-c75fe6fae01b', 10, '10.6', 'Enhanced representation for developing countries', 'Ensure enhanced representation and voice for developing countries in decision-making in global international economic and financial institutions in order to deliver more effective, credible, accountable and legitimate institutions', '2025-08-01 08:46:29.011038+00', '2025-08-01 08:46:29.011038+00'),
('7ad0a1b8-52be-4401-820a-3703cdd8375b', 10, '10.7', 'Facilitate safe migration', 'Facilitate orderly, safe, regular and responsible migration and mobility of people, including through the implementation of planned and well-managed migration policies', '2025-08-01 08:46:29.044049+00', '2025-08-01 08:46:29.044049+00'),
('db4c8a0f-d805-4b89-97ec-5449ecc9f5c0', 10, '10.a', 'Special treatment for least developed countries', 'Implement the principle of special and differential treatment for developing countries, in particular least developed countries, in accordance with World Trade Organization agreements', '2025-08-01 08:46:29.077029+00', '2025-08-01 08:46:29.077029+00'),
('91e04c68-22e0-4d30-b5bd-020073190be6', 10, '10.b', 'Encourage development assistance', 'Encourage official development assistance and financial flows, including foreign direct investment, to States where the need is greatest, in particular least developed countries, African countries, small island developing States and landlocked developing countries, in accordance with their national plans and programmes', '2025-08-01 08:46:29.110492+00', '2025-08-01 08:46:29.110492+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('3a38399c-c8fb-4f7b-8a20-c53f0d64d109', 10, '10.c', 'Reduce transaction costs of remittances', 'By 2030, reduce to less than 3 per cent the transaction costs of migrant remittances and eliminate remittance corridors with costs higher than 5 per cent', '2025-08-01 08:46:29.142489+00', '2025-08-01 08:46:29.142489+00'),
('e1aad622-343c-4bfa-9d7f-7bdd7274271b', 11, '11.1', 'Safe and affordable housing', 'By 2030, ensure access for all to adequate, safe and affordable housing and basic services and upgrade slums', '2025-08-01 08:46:29.175667+00', '2025-08-01 08:46:29.175667+00'),
('e6f5ccff-b743-4f5c-b12a-5bc0d9b088d2', 11, '11.2', 'Sustainable transport systems', 'By 2030, provide access to safe, affordable, accessible and sustainable transport systems for all, improving road safety, notably by expanding public transport, with special attention to the needs of those in vulnerable situations, women, children, persons with disabilities and older persons', '2025-08-01 08:46:29.208763+00', '2025-08-01 08:46:29.208763+00'),
('ccb41922-5998-473c-868e-499750e5c069', 11, '11.3', 'Inclusive urbanization', 'By 2030, enhance inclusive and sustainable urbanization and capacity for participatory, integrated and sustainable human settlement planning and management in all countries', '2025-08-01 08:46:29.240389+00', '2025-08-01 08:46:29.240389+00'),
('f53546ae-76c4-4d4c-9871-47af8c90ad76', 11, '11.4', 'Protect cultural heritage', 'Strengthen efforts to protect and safeguard the world''s cultural and natural heritage', '2025-08-01 08:46:29.273452+00', '2025-08-01 08:46:29.273452+00'),
('4540e119-736c-465f-bbc4-440bcde6875d', 11, '11.5', 'Reduce disaster losses', 'By 2030, significantly reduce the number of deaths and the number of people affected and substantially decrease the direct economic losses relative to global gross domestic product caused by disasters, including water-related disasters, with a focus on protecting the poor and people in vulnerable situations', '2025-08-01 08:46:29.305481+00', '2025-08-01 08:46:29.305481+00'),
('c4b5e2c4-c85f-44c5-a74e-659ab2ed579f', 11, '11.6', 'Reduce environmental impact of cities', 'By 2030, reduce the adverse per capita environmental impact of cities, including by paying special attention to air quality and municipal and other waste management', '2025-08-01 08:46:29.338509+00', '2025-08-01 08:46:29.338509+00'),
('2f4f0af7-b4dd-46d4-b1a1-081b6a5f684b', 11, '11.7', 'Provide access to safe spaces', 'By 2030, provide universal access to safe, inclusive and accessible, green and public spaces, in particular for women and children, older persons and persons with disabilities', '2025-08-01 08:46:29.370379+00', '2025-08-01 08:46:29.370379+00'),
('44d47fb9-3ab2-48f1-9da7-3e8ac5392bb0', 11, '11.a', 'Support positive urban-rural linkages', 'Support positive economic, social and environmental links between urban, peri-urban and rural areas by strengthening national and regional development planning', '2025-08-01 08:46:29.402417+00', '2025-08-01 08:46:29.402417+00'),
('74465468-5e19-4f55-ad2f-9489bd6af947', 11, '11.b', 'Implement integrated policies', 'By 2020, substantially increase the number of cities and human settlements adopting and implementing integrated policies and plans towards inclusion, resource efficiency, mitigation and adaptation to climate change, resilience to disasters, and develop and implement, in line with the Sendai Framework for Disaster Risk Reduction 2015-2030, holistic disaster risk management at all levels', '2025-08-01 08:46:29.437052+00', '2025-08-01 08:46:29.437052+00'),
('d46e2018-53fd-4e6e-9754-6fd385669e22', 11, '11.c', 'Support least developed countries', 'Support least developed countries, including through financial and technical assistance, in building sustainable and resilient buildings utilizing local materials', '2025-08-01 08:46:29.470375+00', '2025-08-01 08:46:29.470375+00'),
('26336794-159c-4826-b3c9-41c48985879d', 12, '12.1', 'Implement sustainable consumption patterns', 'Implement the 10-year framework of programmes on sustainable consumption and production, all countries taking action, with developed countries taking the lead, taking into account the development and capabilities of developing countries', '2025-08-01 08:46:29.503358+00', '2025-08-01 08:46:29.503358+00'),
('5ca93254-b3bc-4b9f-a2d0-86d5a2bb1f5c', 12, '12.2', 'Sustainable management of natural resources', 'By 2030, achieve the sustainable management and efficient use of natural resources', '2025-08-01 08:46:29.535477+00', '2025-08-01 08:46:29.535477+00'),
('b1779f4f-61a6-48bc-ba12-93ed233f072a', 12, '12.3', 'Halve food waste', 'By 2030, halve per capita global food waste at the retail and consumer levels and reduce food losses along production and supply chains, including post-harvest losses', '2025-08-01 08:46:29.567388+00', '2025-08-01 08:46:29.567388+00'),
('fac59f97-aa67-43e4-aedc-ab10f06c2259', 12, '12.4', 'Responsible management of chemicals', 'By 2020, achieve the environmentally sound management of chemicals and all wastes throughout their life cycle, in accordance with agreed international frameworks, and significantly reduce their release to air, water and soil in order to minimize their adverse impacts on human health and the environment', '2025-08-01 08:46:29.605965+00', '2025-08-01 08:46:29.605965+00'),
('756fb478-e0af-4d45-8e2e-40ace3d94791', 12, '12.5', 'Reduce waste generation', 'By 2030, substantially reduce waste generation through prevention, reduction, recycling and reuse', '2025-08-01 08:46:29.639026+00', '2025-08-01 08:46:29.639026+00'),
('869ff415-1dd4-4361-b8e1-c7b02489c4fb', 12, '12.6', 'Encourage sustainable practices', 'Encourage companies, especially large and transnational companies, to adopt sustainable practices and to integrate sustainability information into their reporting cycle', '2025-08-01 08:46:29.672105+00', '2025-08-01 08:46:29.672105+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('b5e562d4-68e0-40b7-8208-6e26c428bf59', 12, '12.7', 'Promote sustainable procurement', 'Promote public procurement practices that are sustainable, in accordance with national policies and priorities', '2025-08-01 08:46:29.705129+00', '2025-08-01 08:46:29.705129+00'),
('0b8441d1-28ed-4582-b527-b015a80e2936', 12, '12.8', 'Promote awareness of sustainable development', 'By 2030, ensure that people everywhere have the relevant information and awareness for sustainable development and lifestyles in harmony with nature', '2025-08-01 08:46:29.740583+00', '2025-08-01 08:46:29.740583+00'),
('e2082200-ac7e-4099-a8ca-61938ecf99c0', 12, '12.a', 'Support developing countries'' scientific capacity', 'Support developing countries to strengthen their scientific and technological capacity to move towards more sustainable patterns of consumption and production', '2025-08-01 08:46:29.773943+00', '2025-08-01 08:46:29.773943+00'),
('ae287afc-b03f-43e2-b719-371fad27d78f', 12, '12.b', 'Develop sustainable tourism tools', 'Develop and implement tools to monitor sustainable development impacts for sustainable tourism that creates jobs and promotes local culture and products', '2025-08-01 08:46:29.807015+00', '2025-08-01 08:46:29.807015+00'),
('06b4256d-64a6-438a-a382-54f1743c6565', 12, '12.c', 'Remove market distortions in fossil fuels', 'Rationalize inefficient fossil-fuel subsidies that encourage wasteful consumption by removing market distortions, in accordance with national circumstances, including by restructuring taxation and phasing out those harmful subsidies, where they exist, to reflect their environmental impacts, taking fully into account the specific needs and conditions of developing countries and minimizing the possible adverse impacts on their development in a manner that protects the poor and the affected communities', '2025-08-01 08:46:29.840301+00', '2025-08-01 08:46:29.840301+00'),
('d051fdf6-7b7d-4cb9-b1cd-afafbdf64576', 13, '13.1', 'Strengthen resilience to climate hazards', 'Strengthen resilience and adaptive capacity to climate-related hazards and natural disasters in all countries', '2025-08-01 08:46:29.873453+00', '2025-08-01 08:46:29.873453+00'),
('cd50f164-706f-4e83-9cd1-41d87650bd85', 13, '13.2', 'Integrate climate measures', 'Integrate climate change measures into national policies, strategies and planning', '2025-08-01 08:46:29.905429+00', '2025-08-01 08:46:29.905429+00'),
('cffbb2cd-8316-4c2f-bfce-514d773cac7e', 13, '13.3', 'Improve education on climate change', 'Improve education, awareness-raising and human and institutional capacity on climate change mitigation, adaptation, impact reduction and early warning', '2025-08-01 08:46:29.938438+00', '2025-08-01 08:46:29.938438+00'),
('4badd1a5-28d4-45f1-ac49-efcc146ed6f9', 13, '13.a', 'Implement climate finance commitment', 'Implement the commitment undertaken by developed-country parties to the United Nations Framework Convention on Climate Change to a goal of mobilizing jointly $100 billion annually by 2020 from all sources to address the needs of developing countries in the context of meaningful mitigation actions and transparency on implementation and fully operationalize the Green Climate Fund through its capitalization as soon as possible', '2025-08-01 08:46:29.971518+00', '2025-08-01 08:46:29.971518+00'),
('46ef2afe-4ef3-4b10-bcf8-c4ff187c5848', 13, '13.b', 'Promote mechanisms for climate planning', 'Promote mechanisms for raising capacity for effective climate change-related planning and management in least developed countries and small island developing States, including focusing on women, youth and local and marginalized communities', '2025-08-01 08:46:30.005529+00', '2025-08-01 08:46:30.005529+00'),
('1a495ce3-2b1c-4ebd-8cf3-b697f9fe7da0', 14, '14.1', 'Reduce marine pollution', 'By 2025, prevent and significantly reduce marine pollution of all kinds, in particular from land-based activities, including marine debris and nutrient pollution', '2025-08-01 08:46:30.038607+00', '2025-08-01 08:46:30.038607+00'),
('9a2620d5-ad0b-4897-a4bd-f48f9794f17c', 14, '14.2', 'Protect marine ecosystems', 'By 2020, sustainably manage and protect marine and coastal ecosystems to avoid significant adverse impacts, including by strengthening their resilience, and take action for their restoration in order to achieve healthy and productive oceans', '2025-08-01 08:46:30.071801+00', '2025-08-01 08:46:30.071801+00'),
('efa82415-5b63-4418-9f57-874d17ef531b', 14, '14.3', 'Minimize ocean acidification', 'Minimize and address the impacts of ocean acidification, including through enhanced scientific cooperation at all levels', '2025-08-01 08:46:30.105377+00', '2025-08-01 08:46:30.105377+00'),
('7999472c-f8bd-43a2-bd70-a3364d117a44', 14, '14.4', 'Regulate fishing', 'By 2020, effectively regulate harvesting and end overfishing, illegal, unreported and unregulated fishing and destructive fishing practices and implement science-based management plans, in order to restore fish stocks in the shortest time feasible, at least to levels that can produce maximum sustainable yield as determined by their biological characteristics', '2025-08-01 08:46:30.137373+00', '2025-08-01 08:46:30.137373+00'),
('0cc7ef48-a2d9-4e8e-81b0-bed50fa7a595', 14, '14.5', 'Conserve marine areas', 'By 2020, conserve at least 10 per cent of coastal and marine areas, consistent with national and international law and based on the best available scientific information', '2025-08-01 08:46:30.170416+00', '2025-08-01 08:46:30.170416+00'),
('99c3f43a-d7c5-4c49-b475-954ca51e4821', 17, '17.1', 'Strengthen domestic revenue collection', 'Strengthen domestic resource mobilization, including through international support to developing countries, to improve domestic capacity for tax and other revenue collection', '2025-08-01 08:46:31.172382+00', '2025-08-01 08:46:31.172382+00'),
('b6c66216-61fc-483e-bf74-8c40d226800e', 14, '14.6', 'End subsidies contributing to overfishing', 'By 2020, prohibit certain forms of fisheries subsidies which contribute to overcapacity and overfishing, eliminate subsidies that contribute to illegal, unreported and unregulated fishing and refrain from introducing new such subsidies, recognizing that appropriate and effective special and differential treatment for developing and least developed countries should be an integral part of the World Trade Organization fisheries subsidies negotiation', '2025-08-01 08:46:30.204333+00', '2025-08-01 08:46:30.204333+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('3e77a620-05bd-4946-b148-de278ef37924', 14, '14.7', 'Increase economic benefits from marine resources', 'By 2030, increase the economic benefits to Small Island developing States and least developed countries from the sustainable use of marine resources, including through sustainable management of fisheries, aquaculture and tourism', '2025-08-01 08:46:30.236473+00', '2025-08-01 08:46:30.236473+00'),
('4805fbce-2bf3-4178-bcc6-af7d0da42da6', 14, '14.a', 'Increase scientific knowledge', 'Increase scientific knowledge, develop research capacity and transfer marine technology, taking into account the Intergovernmental Oceanographic Commission Criteria and Guidelines on the Transfer of Marine Technology, in order to improve ocean health and to enhance the contribution of marine biodiversity to the development of developing countries, in particular small island developing States and least developed countries', '2025-08-01 08:46:30.270138+00', '2025-08-01 08:46:30.270138+00'),
('fc596b07-ce57-425c-9baa-5b84648fa58b', 14, '14.b', 'Provide access for small-scale fishers', 'Provide access for small-scale artisanal fishers to marine resources and markets', '2025-08-01 08:46:30.303281+00', '2025-08-01 08:46:30.303281+00'),
('ae361798-ec7a-4820-9abc-461aec8c8c42', 14, '14.c', 'Enhance conservation through international law', 'Enhance the conservation and sustainable use of oceans and their resources by implementing international law as reflected in UNCLOS, which provides the legal framework for the conservation and sustainable use of oceans and their resources, as recalled in paragraph 158 of The Future We Want', '2025-08-01 08:46:30.336284+00', '2025-08-01 08:46:30.336284+00'),
('83d41212-438d-4ebb-aa8f-25d523ffc53d', 15, '15.1', 'Conserve terrestrial ecosystems', 'By 2020, ensure the conservation, restoration and sustainable use of terrestrial and inland freshwater ecosystems and their services, in particular forests, wetlands, mountains and drylands, in line with obligations under international agreements', '2025-08-01 08:46:30.368485+00', '2025-08-01 08:46:30.368485+00'),
('dfbeafb9-cb33-4a16-881f-a93128638e26', 15, '15.2', 'End deforestation', 'By 2020, promote the implementation of sustainable management of all types of forests, halt deforestation, restore degraded forests and substantially increase afforestation and reforestation globally', '2025-08-01 08:46:30.401315+00', '2025-08-01 08:46:30.401315+00'),
('eac034f6-2498-4a13-bd0f-85a1d9d04418', 15, '15.3', 'Combat desertification', 'By 2030, combat desertification, restore degraded land and soil, including land affected by desertification, drought and floods, and strive to achieve a land degradation-neutral world', '2025-08-01 08:46:30.434643+00', '2025-08-01 08:46:30.434643+00'),
('0e78a6ee-a6e7-4006-bb45-bf6141541f9b', 15, '15.4', 'Conserve mountain ecosystems', 'By 2030, ensure the conservation of mountain ecosystems, including their biodiversity, in order to enhance their capacity to provide benefits that are essential for sustainable development', '2025-08-01 08:46:30.467707+00', '2025-08-01 08:46:30.467707+00'),
('3249450d-f951-4f80-85a1-a988a6b99446', 15, '15.5', 'Protect biodiversity', 'Take urgent and significant action to reduce the degradation of natural habitats, halt the loss of biodiversity and, by 2020, protect and prevent the extinction of threatened species', '2025-08-01 08:46:30.500557+00', '2025-08-01 08:46:30.500557+00'),
('9e9908d7-522c-4b17-8f54-a83c88e12d70', 15, '15.6', 'Promote access to genetic resources', 'Promote fair and equitable sharing of the benefits arising from the utilization of genetic resources and promote appropriate access to such resources, as internationally agreed', '2025-08-01 08:46:30.534404+00', '2025-08-01 08:46:30.534404+00'),
('8db57d9c-a33c-4a06-987d-d478c8c5cc9c', 15, '15.7', 'End poaching and trafficking', 'Take urgent action to end poaching and trafficking of protected species of flora and fauna and address both demand and supply of illegal wildlife products', '2025-08-01 08:46:30.568094+00', '2025-08-01 08:46:30.568094+00'),
('eff60e29-99ea-4e82-9fdb-f7e661571f4b', 15, '15.8', 'Prevent invasive species', 'By 2020, introduce measures to prevent the introduction and significantly reduce the impact of invasive alien species on land and water ecosystems and control or eradicate the priority species', '2025-08-01 08:46:30.601468+00', '2025-08-01 08:46:30.601468+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('43887089-8f21-4641-a7d8-6abcf46f9b55', 15, '15.9', 'Integrate ecosystem values', 'By 2020, integrate ecosystem and biodiversity values into national and local planning, development processes, poverty reduction strategies and accounts', '2025-08-01 08:46:30.637831+00', '2025-08-01 08:46:30.637831+00'),
('edfe6e1a-fc7a-41e4-9c0d-d23d2690394a', 15, '15.a', 'Mobilize resources for biodiversity', 'Mobilize and significantly increase financial resources from all sources to conserve and sustainably use biodiversity and ecosystems', '2025-08-01 08:46:30.670961+00', '2025-08-01 08:46:30.670961+00'),
('da753739-d6d9-46e9-9b06-118f4b3bd9a4', 15, '15.b', 'Finance sustainable forest management', 'Mobilize significant resources from all sources and at all levels to finance sustainable forest management and provide adequate incentives to developing countries to advance such management, including for conservation and reforestation', '2025-08-01 08:46:30.704126+00', '2025-08-01 08:46:30.704126+00'),
('2fda675d-3c1c-46c4-82b6-c8e7ef0e92c2', 15, '15.c', 'Combat poaching and trafficking', 'Enhance global support for efforts to combat poaching and trafficking of protected species, including by increasing the capacity of local communities to pursue sustainable livelihood opportunities', '2025-08-01 08:46:30.737439+00', '2025-08-01 08:46:30.737439+00'),
('1756427f-84bf-412f-ac5b-2b4111de7d63', 16, '16.1', 'Reduce violence', 'Significantly reduce all forms of violence and related death rates everywhere', '2025-08-01 08:46:30.770673+00', '2025-08-01 08:46:30.770673+00'),
('ad7e2cad-d673-41bc-ba5e-ef816e93ebfe', 16, '16.2', 'End abuse and trafficking of children', 'End abuse, exploitation, trafficking and all forms of violence against and torture of children', '2025-08-01 08:46:30.803885+00', '2025-08-01 08:46:30.803885+00'),
('a6abbb62-52ed-46e0-bcac-45f48771f649', 16, '16.3', 'Promote rule of law', 'Promote the rule of law at the national and international levels and ensure equal access to justice for all', '2025-08-01 08:46:30.836853+00', '2025-08-01 08:46:30.836853+00'),
('be38c51c-3ebd-4a49-b7d7-4fcfef2336a5', 16, '16.4', 'Combat illicit financial flows', 'By 2030, significantly reduce illicit financial and arms flows, strengthen the recovery and return of stolen assets and combat all forms of organized crime', '2025-08-01 08:46:30.870243+00', '2025-08-01 08:46:30.870243+00'),
('a0c87ef3-98c8-4186-a046-0d5a55ac8f4a', 16, '16.5', 'Reduce corruption and bribery', 'Substantially reduce corruption and bribery in all their forms', '2025-08-01 08:46:30.902991+00', '2025-08-01 08:46:30.902991+00'),
('e303c738-406f-4b35-9fb2-11bbdde61629', 16, '16.6', 'Develop effective institutions', 'Develop effective, accountable and transparent institutions at all levels', '2025-08-01 08:46:30.93619+00', '2025-08-01 08:46:30.93619+00'),
('186450af-8ad4-48f6-87ff-4c8d77a07509', 16, '16.7', 'Ensure responsive decision-making', 'Ensure responsive, inclusive, participatory and representative decision-making at all levels', '2025-08-01 08:46:30.969278+00', '2025-08-01 08:46:30.969278+00'),
('500370dd-6710-495a-a3fb-b582239f0b72', 16, '16.8', 'Broaden participation in global governance', 'Broaden and strengthen the participation of developing countries in the institutions of global governance', '2025-08-01 08:46:31.004864+00', '2025-08-01 08:46:31.004864+00'),
('7323432c-7419-4492-9021-a553549afb6c', 16, '16.9', 'Legal identity for all', 'By 2030, provide legal identity for all, including birth registration', '2025-08-01 08:46:31.03827+00', '2025-08-01 08:46:31.03827+00'),
('b7cfb38e-9607-42cd-bf40-2bdc4431c019', 16, '16.10', 'Ensure public access to information', 'Ensure public access to information and protect fundamental freedoms, in accordance with national legislation and international agreements', '2025-08-01 08:46:31.071348+00', '2025-08-01 08:46:31.071348+00'),
('7e301c43-bb6f-47ee-827f-a2f330c62aa8', 16, '16.a', 'Strengthen institutions to prevent violence', 'Strengthen relevant national institutions, including through international cooperation, for building capacity at all levels, in particular in developing countries, to prevent violence and combat terrorism and crime', '2025-08-01 08:46:31.106184+00', '2025-08-01 08:46:31.106184+00'),
('e37119e6-369b-4be4-b7c1-068046890dbe', 16, '16.b', 'Promote non-discriminatory laws', 'Promote and enforce non-discriminatory laws and policies for sustainable development', '2025-08-01 08:46:31.139436+00', '2025-08-01 08:46:31.139436+00'),
('e8b9423a-c7e4-463c-b8e2-8c7f4f111886', 17, '17.2', 'Implement ODA commitments', 'Developed countries to implement fully their official development assistance commitments, including the commitment by many developed countries to achieve the target of 0.7 per cent of gross national income for official development assistance (ODA/GNI) to developing countries and 0.15 to 0.20 per cent of ODA/GNI to least developed countries', '2025-08-01 08:46:31.204474+00', '2025-08-01 08:46:31.204474+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('ebe29a0d-5fab-4853-bc78-a996e2d06bcf', 17, '17.3', 'Mobilize financial resources', 'Mobilize additional financial resources for developing countries from multiple sources', '2025-08-01 08:46:31.237325+00', '2025-08-01 08:46:31.237325+00'),
('9e37797c-b915-424a-a9fd-122562986f12', 17, '17.4', 'Assist developing countries in debt sustainability', 'Assist developing countries in attaining long-term debt sustainability through coordinated policies aimed at fostering debt financing, debt relief and debt restructuring, as appropriate, and address the external debt of highly indebted poor countries to reduce debt distress', '2025-08-01 08:46:31.270864+00', '2025-08-01 08:46:31.270864+00'),
('981a6ead-6000-4bde-8f02-b7d59ea9ca2c', 17, '17.5', 'Adopt investment promotion regimes', 'Adopt and implement investment promotion regimes for least developed countries', '2025-08-01 08:46:31.304097+00', '2025-08-01 08:46:31.304097+00'),
('544e4611-1b7a-4928-8a55-6884b7af0098', 17, '17.6', 'Enhance cooperation on science and technology', 'Enhance North-South, South-South and triangular regional and international cooperation on and access to science, technology and innovation and enhance knowledge sharing on mutually agreed terms, including through improved coordination among existing mechanisms, in particular at the United Nations level, and through a global technology facilitation mechanism', '2025-08-01 08:46:31.337285+00', '2025-08-01 08:46:31.337285+00'),
('b3974f4f-2877-4365-b616-9ca6d0f587b3', 17, '17.7', 'Promote sustainable technologies', 'Promote the development, transfer, dissemination and diffusion of environmentally sound technologies to developing countries on favourable terms, including on concessional and preferential terms, as mutually agreed', '2025-08-01 08:46:31.370282+00', '2025-08-01 08:46:31.370282+00'),
('43c71f8a-da28-4f68-8b79-2c506a8ba5ab', 17, '17.8', 'Operationalize technology bank', 'Fully operationalize the technology bank and science, technology and innovation capacity-building mechanism for least developed countries by 2017 and enhance the use of enabling technology, in particular information and communications technology', '2025-08-01 08:46:31.403532+00', '2025-08-01 08:46:31.403532+00'),
('ec5b8ccb-8008-493e-bf54-eb3a5e25f6ef', 17, '17.9', 'Enhance capacity-building support', 'Enhance international support for implementing effective and targeted capacity-building in developing countries to support national plans to implement all the sustainable development goals, including through North-South, South-South and triangular cooperation', '2025-08-01 08:46:31.436931+00', '2025-08-01 08:46:31.436931+00'),
('85d6c9a7-563b-4039-bcae-73faaec7302e', 17, '17.10', 'Promote universal trading system', 'Promote a universal, rules-based, open, non-discriminatory and equitable multilateral trading system under the World Trade Organization, including through the conclusion of negotiations under its Doha Development Agenda', '2025-08-01 08:46:31.47159+00', '2025-08-01 08:46:31.47159+00'),
('8ead50c3-47eb-435f-a6e7-547d8cee01dd', 17, '17.11', 'Increase developing country exports', 'Significantly increase the exports of developing countries, in particular with a view to doubling the least developed countries'' share of global exports by 2020', '2025-08-01 08:46:31.504785+00', '2025-08-01 08:46:31.504785+00'),
('287491a2-35e0-441e-bb53-0ffc19bff069', 17, '17.12', 'Realize timely duty-free access', 'Realize timely implementation of duty-free and quota-free market access on a lasting basis for all least developed countries, consistent with World Trade Organization decisions, including by ensuring that preferential rules of origin applicable to imports from least developed countries are transparent and simple, and contribute to facilitating market access', '2025-08-01 08:46:31.537781+00', '2025-08-01 08:46:31.537781+00'),
('1271820f-16c1-4cbd-836b-c02fccc24bcd', 17, '17.13', 'Enhance macroeconomic stability', 'Enhance global macroeconomic stability, including through policy coordination and policy coherence', '2025-08-01 08:46:31.57085+00', '2025-08-01 08:46:31.57085+00'),
('134114b8-7929-4df8-b3ca-808ad9e3cca2', 17, '17.14', 'Enhance policy coherence', 'Enhance policy coherence for sustainable development', '2025-08-01 08:46:31.603667+00', '2025-08-01 08:46:31.603667+00'),
('9021d21d-5247-48d3-b37a-9d73a5ca2d64', 17, '17.15', 'Respect policy space', 'Respect each country''s policy space and leadership to establish and implement policies for poverty eradication and sustainable development', '2025-08-01 08:46:31.6354+00', '2025-08-01 08:46:31.6354+00'),
('8ed290b5-d3d0-44f3-be46-3c54fad4c5a3', 17, '17.16', 'Enhance global partnership', 'Enhance the global partnership for sustainable development, complemented by multi-stakeholder partnerships that mobilize and share knowledge, expertise, technology and financial resources, to support the achievement of the sustainable development goals in all countries, in particular developing countries', '2025-08-01 08:46:31.668241+00', '2025-08-01 08:46:31.668241+00'),
('6788e5c2-0334-4807-9b52-71beecf2e948', 17, '17.17', 'Encourage effective partnerships', 'Encourage and promote effective public, public-private and civil society partnerships, building on the experience and resourcing strategies of partnerships', '2025-08-01 08:46:31.70122+00', '2025-08-01 08:46:31.70122+00'),
('0dc49995-8143-4423-aef1-409c843dc38d', 17, '17.18', 'Enhance data availability', 'By 2020, enhance capacity-building support to developing countries, including for least developed countries and small island developing States, to increase significantly the availability of high-quality, timely and reliable data disaggregated by income, gender, age, race, ethnicity, migratory status, disability, geographic location and other characteristics relevant in national contexts', '2025-08-01 08:46:31.734326+00', '2025-08-01 08:46:31.734326+00'),
('96e902d8-dc5c-47fb-9985-2e802cddc56b', 17, '17.19', 'Develop broader progress measures', 'By 2030, build on existing initiatives to develop measurements of progress on sustainable development that complement gross domestic product, and support statistical capacity-building in developing countries', '2025-08-01 08:46:31.766374+00', '2025-08-01 08:46:31.766374+00'),
('5fe74dd2-0874-4bf8-9c10-aa3b5859f7d0', 1, '1.1', 'Eradicate extreme poverty', 'By 2030, eradicate extreme poverty for all people everywhere, currently measured as people living on less than $1.25 a day', '2025-08-01 08:56:48.881604+00', '2025-08-01 08:56:48.881604+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('0f21123b-1405-453f-9a27-f3050d9de032', 1, '1.2', 'Reduce poverty by half', 'By 2030, reduce at least by half the proportion of men, women and children of all ages living in poverty in all its dimensions according to national definitions', '2025-08-01 08:56:48.920641+00', '2025-08-01 08:56:48.920641+00'),
('581ffd99-20f8-4f19-a8df-532fee8c82b8', 1, '1.3', 'Social protection systems', 'Implement nationally appropriate social protection systems and measures for all, including floors, and by 2030 achieve substantial coverage of the poor and the vulnerable', '2025-08-01 08:56:48.953866+00', '2025-08-01 08:56:48.953866+00'),
('a4b19546-5c2b-49f2-9f1b-1f18020efe1a', 1, '1.4', 'Equal access to basic services', 'By 2030, ensure that all men and women, in particular the poor and the vulnerable, have equal rights to economic resources, as well as access to basic services, ownership and control over land and other forms of property, inheritance, natural resources, appropriate new technology and financial services, including microfinance', '2025-08-01 08:56:48.98702+00', '2025-08-01 08:56:48.98702+00'),
('4978a50e-1a19-41ae-958e-fda5b7769f07', 1, '1.5', 'Resilience to disasters', 'By 2030, build the resilience of the poor and those in vulnerable situations and reduce their exposure and vulnerability to climate-related extreme events and other economic, social and environmental shocks and disasters', '2025-08-01 08:56:49.020155+00', '2025-08-01 08:56:49.020155+00'),
('56758eb0-c69f-4a68-9aa7-0abeaf8d86ab', 1, '1.a', 'Resource mobilization', 'Ensure significant mobilization of resources from a variety of sources, including through enhanced development cooperation, in order to provide adequate and predictable means for developing countries, in particular least developed countries, to implement programmes and policies to end poverty in all its dimensions', '2025-08-01 08:56:49.05327+00', '2025-08-01 08:56:49.05327+00'),
('a17256c8-496f-46af-9e09-29e2418fd615', 1, '1.b', 'Policy frameworks', 'Create sound policy frameworks at the national, regional and international levels, based on pro-poor and gender-sensitive development strategies, to support accelerated investment in poverty eradication actions', '2025-08-01 08:56:49.086796+00', '2025-08-01 08:56:49.086796+00'),
('e20712b5-427e-4576-bed8-3694f346533f', 2, '2.1', 'End hunger', 'By 2030, end hunger and ensure access by all people, in particular the poor and people in vulnerable situations, including infants, to safe, nutritious and sufficient food all year round', '2025-08-01 08:56:49.119811+00', '2025-08-01 08:56:49.119811+00'),
('73e7f689-95d9-403d-84a3-b29fa77ac1b9', 2, '2.2', 'End malnutrition', 'By 2030, end all forms of malnutrition, including achieving, by 2025, the internationally agreed targets on stunting and wasting in children under 5 years of age, and address the nutritional needs of adolescent girls, pregnant and lactating women and older persons', '2025-08-01 08:56:49.154074+00', '2025-08-01 08:56:49.154074+00'),
('9f183cee-6873-4569-a126-c609319cd055', 2, '2.3', 'Double agricultural productivity', 'By 2030, double the agricultural productivity and incomes of small-scale food producers, in particular women, indigenous peoples, family farmers, pastoralists and fishers, including through secure and equal access to land, other productive resources and inputs, knowledge, financial services, markets and opportunities for value addition and non-farm employment', '2025-08-01 08:56:49.188321+00', '2025-08-01 08:56:49.188321+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('012b0708-7521-498f-ad3d-6a898ffbf53a', 2, '2.4', 'Sustainable food production', 'By 2030, ensure sustainable food production systems and implement resilient agricultural practices that increase productivity and production, that help maintain ecosystems, that strengthen capacity for adaptation to climate change, extreme weather, drought, flooding and other disasters and that progressively improve land and soil quality', '2025-08-01 08:56:49.222074+00', '2025-08-01 08:56:49.222074+00'),
('0e40d50e-5654-4820-a593-d7c802e0af76', 2, '2.5', 'Maintain genetic diversity', 'By 2020, maintain the genetic diversity of seeds, cultivated plants and farmed and domesticated animals and their related wild species, including through soundly managed and diversified seed and plant banks at the national, regional and international levels, and promote access to and fair and equitable sharing of benefits arising from the utilization of genetic resources and associated traditional knowledge, as internationally agreed', '2025-08-01 08:56:49.255336+00', '2025-08-01 08:56:49.255336+00'),
('b2eb6770-9976-44df-9e92-558c27561c1a', 2, '2.a', 'Increase agricultural investment', 'Increase investment, including through enhanced international cooperation, in rural infrastructure, agricultural research and extension services, technology development and plant and livestock gene banks in order to enhance agricultural productive capacity in developing countries, in particular least developed countries', '2025-08-01 08:56:49.288605+00', '2025-08-01 08:56:49.288605+00'),
('ea929c36-ef86-432e-aca5-34014b75e1f9', 2, '2.b', 'Correct trade restrictions', 'Correct and prevent trade restrictions and distortions in world agricultural markets, including through the parallel elimination of all forms of agricultural export subsidies and all export measures with equivalent effect, in accordance with the mandate of the Doha Development Round', '2025-08-01 08:56:49.321252+00', '2025-08-01 08:56:49.321252+00'),
('1e40b4f1-f77e-4a6e-b358-4aa0dede2440', 2, '2.c', 'Food commodity markets', 'Adopt measures to ensure the proper functioning of food commodity markets and their derivatives and facilitate timely access to market information, including on food reserves, in order to help limit extreme food price volatility', '2025-08-01 08:56:49.354759+00', '2025-08-01 08:56:49.354759+00'),
('fa2889bc-aff3-4c7e-9542-7f0b8204e960', 3, '3.1', 'Reduce maternal mortality', 'By 2030, reduce the global maternal mortality ratio to less than 70 per 100,000 live births', '2025-08-01 08:56:49.387901+00', '2025-08-01 08:56:49.387901+00'),
('890a615d-f621-405c-b651-a291eaf5237d', 3, '3.2', 'End preventable deaths of children', 'By 2030, end preventable deaths of newborns and children under 5 years of age, with all countries aiming to reduce neonatal mortality to at least as low as 12 per 1,000 live births and under5 mortality to at least as low as 25 per 1,000 live births', '2025-08-01 08:56:49.421108+00', '2025-08-01 08:56:49.421108+00'),
('292c1ed2-0010-4bbe-9163-4a4dd97da707', 3, '3.3', 'End epidemics', 'By 2030, end the epidemics of AIDS, tuberculosis, malaria and neglected tropical diseases and combat hepatitis, water-borne diseases and other communicable diseases', '2025-08-01 08:56:49.456854+00', '2025-08-01 08:56:49.456854+00'),
('fc885b39-633c-4746-a288-68729b7db7e4', 3, '3.4', 'Reduce non-communicable diseases', 'By 2030, reduce by one third premature mortality from non-communicable diseases through prevention and treatment and promote mental health and well-being', '2025-08-01 08:56:49.489902+00', '2025-08-01 08:56:49.489902+00'),
('c0d16590-68c0-4e32-97ab-bf0e78518456', 3, '3.5', 'Strengthen substance abuse prevention', 'Strengthen the prevention and treatment of substance abuse, including narcotic drug abuse and harmful use of alcohol', '2025-08-01 08:56:49.523524+00', '2025-08-01 08:56:49.523524+00'),
('678224c5-95f9-4585-9a51-fc35cfd013a4', 3, '3.6', 'Halve road traffic deaths', 'By 2020, halve the number of global deaths and injuries from road traffic accidents', '2025-08-01 08:56:49.555346+00', '2025-08-01 08:56:49.555346+00'),
('7078d1b9-ca33-4135-8797-6e3fcabd1023', 3, '3.7', 'Universal reproductive health', 'By 2030, ensure universal access to sexual and reproductive health-care services, including for family planning, information and education, and the integration of reproductive health into national strategies and programmes', '2025-08-01 08:56:49.587268+00', '2025-08-01 08:56:49.587268+00'),
('b4bded1b-6e06-4b9d-a6ee-eb9794792d67', 3, '3.8', 'Universal health coverage', 'Achieve universal health coverage, including financial risk protection, access to quality essential health-care services and access to safe, effective, quality and affordable essential medicines and vaccines for all', '2025-08-01 08:56:49.621269+00', '2025-08-01 08:56:49.621269+00'),
('ee14010d-6ff3-47f6-9e73-9bb66b696dad', 3, '3.9', 'Reduce pollution deaths', 'By 2030, substantially reduce the number of deaths and illnesses from hazardous chemicals and air, water and soil pollution and contamination', '2025-08-01 08:56:49.654149+00', '2025-08-01 08:56:49.654149+00'),
('18406fa8-a1c5-4016-9f48-a28e5ba24aba', 3, '3.a', 'Tobacco control', 'Strengthen the implementation of the World Health Organization Framework Convention on Tobacco Control in all countries, as appropriate', '2025-08-01 08:56:49.687536+00', '2025-08-01 08:56:49.687536+00'),
('a5781708-197d-484c-a93c-823d00304756', 3, '3.b', 'Support vaccine research', 'Support the research and development of vaccines and medicines for the communicable and noncommunicable diseases that primarily affect developing countries, provide access to affordable essential medicines and vaccines, in accordance with the Doha Declaration on the TRIPS Agreement and Public Health', '2025-08-01 08:56:49.720565+00', '2025-08-01 08:56:49.720565+00'),
('ea376369-9b9c-492b-8c94-d32817c405da', 3, '3.c', 'Increase health financing', 'Substantially increase health financing and the recruitment, development, training and retention of the health workforce in developing countries, especially in least developed countries and small island developing States', '2025-08-01 08:56:49.753555+00', '2025-08-01 08:56:49.753555+00'),
('c8cb226f-f54e-4559-85ac-ec882e258189', 3, '3.d', 'Strengthen health risk management', 'Strengthen the capacity of all countries, in particular developing countries, for early warning, risk reduction and management of national and global health risks', '2025-08-01 08:56:49.78676+00', '2025-08-01 08:56:49.78676+00'),
('a4ccb1e6-8ff8-4420-bd83-93e3b5ebdee8', 4, '4.1', 'Free primary and secondary education', 'By 2030, ensure that all girls and boys complete free, equitable and quality primary and secondary education leading to relevant and effective learning outcomes', '2025-08-01 08:56:49.819826+00', '2025-08-01 08:56:49.819826+00'),
('132fed4e-1812-4425-a3e7-ade5cee28b73', 4, '4.2', 'Early childhood development', 'By 2030, ensure that all girls and boys have access to quality early childhood development, care and preprimary education so that they are ready for primary education', '2025-08-01 08:56:49.852705+00', '2025-08-01 08:56:49.852705+00'),
('a71f24e7-4b15-44c8-a70d-08121a7efb6b', 4, '4.3', 'Equal access to technical/vocational education', 'By 2030, ensure equal access for all women and men to affordable and quality technical, vocational and tertiary education, including university', '2025-08-01 08:56:49.885618+00', '2025-08-01 08:56:49.885618+00'),
('5ff16591-6c1d-47c5-b19d-add31cc5841a', 4, '4.4', 'Increase relevant skills for employment', 'By 2030, substantially increase the number of youth and adults who have relevant skills, including technical and vocational skills, for employment, decent jobs and entrepreneurship', '2025-08-01 08:56:49.918616+00', '2025-08-01 08:56:49.918616+00'),
('a1977898-146b-4207-9670-9a01024d10b9', 3, '3.3', 'Target 3.3', 'Target 3.3 description', '2025-08-01 10:03:15.130069+00', '2025-08-01 10:03:15.130069+00'),
('c8ebe034-480f-458b-a3f7-288675b612eb', 4, '4.5', 'Eliminate gender disparities', 'By 2030, eliminate gender disparities in education and ensure equal access to all levels of education and vocational training for the vulnerable, including persons with disabilities, indigenous peoples and children in vulnerable situations', '2025-08-01 08:56:49.951753+00', '2025-08-01 08:56:49.951753+00'),
('de3f977b-3dab-40d9-9b71-ad304f7bd3f6', 4, '4.6', 'Adult literacy and numeracy', 'By 2030, ensure that all youth and a substantial proportion of adults, both men and women, achieve literacy and numeracy', '2025-08-01 08:56:49.984785+00', '2025-08-01 08:56:49.984785+00'),
('b389cb29-6075-49b0-85b2-faaed8165515', 4, '4.7', 'Education for sustainable development', 'By 2030, ensure that all learners acquire the knowledge and skills needed to promote sustainable development, including, among others, through education for sustainable development and sustainable lifestyles, human rights, gender equality, promotion of a culture of peace and non-violence, global citizenship and appreciation of cultural diversity', '2025-08-01 08:56:50.017393+00', '2025-08-01 08:56:50.017393+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('54846ed7-0478-4344-a4dc-77c9f4506f8c', 4, '4.a', 'Build and upgrade education facilities', 'Build and upgrade education facilities that are child, disability and gender sensitive and provide safe, non-violent, inclusive and effective learning environments for all', '2025-08-01 08:56:50.050677+00', '2025-08-01 08:56:50.050677+00'),
('4f4dd128-60b6-43fb-a657-a9fb6011876a', 4, '4.b', 'Expand higher education scholarships', 'By 2020, substantially expand globally the number of scholarships available to developing countries, in particular least developed countries, small island developing States and African countries, for enrolment in higher education', '2025-08-01 08:56:50.084176+00', '2025-08-01 08:56:50.084176+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('5b79f46e-2250-4d33-b323-ac3552b4f6d4', 4, '4.c', 'Increase supply of qualified teachers', 'By 2030, substantially increase the supply of qualified teachers, including through international cooperation for teacher training in developing countries, especially least developed countries and small island developing States', '2025-08-01 08:56:50.116982+00', '2025-08-01 08:56:50.116982+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('e1d305d2-cd58-494a-a383-f2528c56f508', 5, '5.1', 'End discrimination against women', 'End all forms of discrimination against all women and girls everywhere', '2025-08-01 08:56:50.150187+00', '2025-08-01 08:56:50.150187+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('cc4c6fb9-ed6a-42ab-b9b4-d28f0be0669e', 5, '5.2', 'Eliminate violence against women', 'Eliminate all forms of violence against all women and girls in the public and private spheres, including trafficking and sexual and other types of exploitation', '2025-08-01 08:56:50.183157+00', '2025-08-01 08:56:50.183157+00'),
('6396b6ce-8f1f-4d3d-a5e5-1b0fb4eb13e3', 5, '5.3', 'Eliminate harmful practices', 'Eliminate all harmful practices, such as child, early and forced marriage and female genital mutilation', '2025-08-01 08:56:50.21628+00', '2025-08-01 08:56:50.21628+00'),
('81c112f9-99d8-4425-955c-a3fc6ff6265b', 5, '5.4', 'Value unpaid care work', 'Recognize and value unpaid care and domestic work through the provision of public services, infrastructure and social protection policies and the promotion of shared responsibility within the household and the family as nationally appropriate', '2025-08-01 08:56:50.249245+00', '2025-08-01 08:56:50.249245+00'),
('33486196-4633-41dd-9b6b-92352d8aa0b0', 5, '5.5', 'Ensure full participation in leadership', 'Ensure women''s full and effective participation and equal opportunities for leadership at all levels of decision-making in political, economic and public life', '2025-08-01 08:56:50.282347+00', '2025-08-01 08:56:50.282347+00'),
('6c581d68-37af-4eaa-ab7a-7572037365d2', 5, '5.6', 'Universal access to reproductive rights', 'Ensure universal access to sexual and reproductive health and reproductive rights as agreed in accordance with the Programme of Action of the International Conference on Population and Development and the Beijing Platform for Action', '2025-08-01 08:56:50.315172+00', '2025-08-01 08:56:50.315172+00'),
('7adf5ef3-cd47-4567-9eac-91bf9d0d616e', 5, '5.a', 'Equal rights to economic resources', 'Undertake reforms to give women equal rights to economic resources, as well as access to ownership and control over land and other forms of property, financial services, inheritance and natural resources, in accordance with national laws', '2025-08-01 08:56:50.348082+00', '2025-08-01 08:56:50.348082+00'),
('408c646c-690f-4ce4-8bdd-294f5ff1791f', 5, '5.b', 'Enhance use of enabling technology', 'Enhance the use of enabling technology, in particular information and communications technology, to promote the empowerment of women', '2025-08-01 08:56:50.381314+00', '2025-08-01 08:56:50.381314+00'),
('4314ecc5-b68b-45c6-8d90-47f3a8a8e9ef', 5, '5.c', 'Adopt policies for gender equality', 'Adopt and strengthen sound policies and enforceable legislation for the promotion of gender equality and the empowerment of all women and girls at all levels', '2025-08-01 08:56:50.414279+00', '2025-08-01 08:56:50.414279+00'),
('9f52d08f-d70c-4ffb-baf1-e83e00dd9292', 6, '6.1', 'Safe and affordable drinking water', 'By 2030, achieve universal and equitable access to safe and affordable drinking water for all', '2025-08-01 08:56:50.446313+00', '2025-08-01 08:56:50.446313+00'),
('b0fc486c-90ed-430e-b333-6bb4fe24e1c1', 6, '6.2', 'Adequate sanitation and hygiene', 'By 2030, achieve access to adequate and equitable sanitation and hygiene for all and end open defecation, paying special attention to the needs of women and girls and those in vulnerable situations', '2025-08-01 08:56:50.478342+00', '2025-08-01 08:56:50.478342+00'),
('2cf5c370-1ee2-4059-a34a-48fb691ddeaa', 6, '6.3', 'Improve water quality', 'By 2030, improve water quality by reducing pollution, eliminating dumping and minimizing release of hazardous chemicals and materials, halving the proportion of untreated wastewater and substantially increasing recycling and safe reuse globally', '2025-08-01 08:56:50.511305+00', '2025-08-01 08:56:50.511305+00'),
('9c687b01-a4a1-4e0a-a559-cddfb018bd69', 6, '6.4', 'Increase water-use efficiency', 'By 2030, substantially increase water-use efficiency across all sectors and ensure sustainable withdrawals and supply of freshwater to address water scarcity and substantially reduce the number of people suffering from water scarcity', '2025-08-01 08:56:50.544261+00', '2025-08-01 08:56:50.544261+00'),
('923159ef-93b4-43a1-a220-a218288ef3af', 6, '6.5', 'Integrated water resources management', 'By 2030, implement integrated water resources management at all levels, including through transboundary cooperation as appropriate', '2025-08-01 08:56:50.577119+00', '2025-08-01 08:56:50.577119+00'),
('3e6d1105-83b1-4c9d-9a39-d38a9e806549', 6, '6.6', 'Protect water-related ecosystems', 'By 2020, protect and restore water-related ecosystems, including mountains, forests, wetlands, rivers, aquifers and lakes', '2025-08-01 08:56:50.609364+00', '2025-08-01 08:56:50.609364+00'),
('a4e37a37-f5aa-4fd7-97e5-9791a9b76745', 6, '6.a', 'Expand water cooperation', 'By 2030, expand international cooperation and capacity-building support to developing countries in water- and sanitation-related activities and programmes, including water harvesting, desalination, water efficiency, wastewater treatment, recycling and reuse technologies', '2025-08-01 08:56:50.642622+00', '2025-08-01 08:56:50.642622+00'),
('63da82e5-2468-488f-ba7e-e08b032a5afe', 6, '6.b', 'Support local participation', 'Support and strengthen the participation of local communities in improving water and sanitation management', '2025-08-01 08:56:50.675688+00', '2025-08-01 08:56:50.675688+00'),
('20bbbf2c-7a7a-4892-a4b8-21b5ebdbe17a', 7, '7.1', 'Universal access to modern energy', 'By 2030, ensure universal access to affordable, reliable and modern energy services', '2025-08-01 08:56:50.708574+00', '2025-08-01 08:56:50.708574+00'),
('a53bc646-1740-487c-8192-43d1c40a538d', 7, '7.2', 'Increase renewable energy', 'By 2030, increase substantially the share of renewable energy in the global energy mix', '2025-08-01 08:56:50.74165+00', '2025-08-01 08:56:50.74165+00'),
('e80f4675-b7ef-4ec2-90ec-41d95d8af1a5', 7, '7.3', 'Double energy efficiency', 'By 2030, double the global rate of improvement in energy efficiency', '2025-08-01 08:56:50.774721+00', '2025-08-01 08:56:50.774721+00'),
('271b5b96-fa1a-4e68-b7cf-83f5bbf6004e', 7, '7.a', 'Enhance energy cooperation', 'By 2030, enhance international cooperation to facilitate access to clean energy research and technology, including renewable energy, energy efficiency and advanced and cleaner fossil-fuel technology, and promote investment in energy infrastructure and clean energy technology', '2025-08-01 08:56:50.80769+00', '2025-08-01 08:56:50.80769+00'),
('ad5b82b6-526b-431e-80bd-d3fb686cff3f', 7, '7.b', 'Expand energy infrastructure', 'By 2030, expand infrastructure and upgrade technology for supplying modern and sustainable energy services for all in developing countries, in particular least developed countries, small island developing States, and land-locked developing countries, in accordance with their respective programmes of support', '2025-08-01 08:56:50.840726+00', '2025-08-01 08:56:50.840726+00'),
('5145c8bc-506a-49e9-8596-9c113a570c64', 8, '8.1', 'Sustain economic growth', 'Sustain per capita economic growth in accordance with national circumstances and, in particular, at least 7 per cent gross domestic product growth per annum in the least developed countries', '2025-08-01 08:56:50.876218+00', '2025-08-01 08:56:50.876218+00'),
('d3f6a637-126c-40c3-82e7-fb2d485f44a4', 8, '8.2', 'Economic productivity through diversification', 'Achieve higher levels of economic productivity through diversification, technological upgrading and innovation, including through a focus on high-value added and labour-intensive sectors', '2025-08-01 08:56:50.909635+00', '2025-08-01 08:56:50.909635+00'),
('257c106d-6eb8-4105-9b94-bba603074b9b', 8, '8.3', 'Promote development-oriented policies', 'Promote development-oriented policies that support productive activities, decent job creation, entrepreneurship, creativity and innovation, and encourage the formalization and growth of micro-, small- and medium-sized enterprises, including through access to financial services', '2025-08-01 08:56:50.943022+00', '2025-08-01 08:56:50.943022+00'),
('67fa3a37-bec7-4fc5-948d-9f891b3f117f', 8, '8.4', 'Improve resource efficiency', 'Improve progressively, through 2030, global resource efficiency in consumption and production and endeavour to decouple economic growth from environmental degradation, in accordance with the 10-year framework of programmes on sustainable consumption and production, with developed countries taking the lead', '2025-08-01 08:56:50.976227+00', '2025-08-01 08:56:50.976227+00'),
('cb34f666-fac2-402a-864c-a92fdb752551', 8, '8.5', 'Full employment and decent work', 'By 2030, achieve full and productive employment and decent work for all women and men, including for young people and persons with disabilities, and equal pay for work of equal value', '2025-08-01 08:56:51.009968+00', '2025-08-01 08:56:51.009968+00'),
('6ab910d6-5ea8-46c3-94c7-fcd63eaad879', 8, '8.6', 'Reduce youth NEET', 'By 2020, substantially reduce the proportion of youth not in employment, education or training', '2025-08-01 08:56:51.04283+00', '2025-08-01 08:56:51.04283+00'),
('cb0878d0-4da0-441f-95f9-7fc367b9edd2', 8, '8.7', 'Eradicate forced labour', 'Take immediate and effective measures to eradicate forced labour, end modern slavery and human trafficking and secure the prohibition and elimination of the worst forms of child labour, including recruitment and use of child soldiers, and by 2025 end child labour in all its forms', '2025-08-01 08:56:51.076018+00', '2025-08-01 08:56:51.076018+00'),
('fdbe30f7-8cd4-4dd6-848a-8360a7606c29', 8, '8.8', 'Protect labour rights', 'Protect labour rights and promote safe and secure working environments for all workers, including migrant workers, in particular women migrants, and those in precarious employment', '2025-08-01 08:56:51.108909+00', '2025-08-01 08:56:51.108909+00'),
('a3740a11-076f-4c01-9752-7d04955a25a4', 8, '8.9', 'Promote sustainable tourism', 'By 2030, devise and implement policies to promote sustainable tourism that creates jobs and promotes local culture and products', '2025-08-01 08:56:51.141876+00', '2025-08-01 08:56:51.141876+00'),
('d17bfa49-d158-4f0b-92cb-dcd05d30c38e', 8, '8.10', 'Strengthen financial institutions', 'Strengthen the capacity of domestic financial institutions to encourage and expand access to banking, insurance and financial services for all', '2025-08-01 08:56:51.174846+00', '2025-08-01 08:56:51.174846+00'),
('4842e66c-039d-45e4-9b3b-a95d5539f529', 8, '8.a', 'Increase Aid for Trade', 'Increase Aid for Trade support for developing countries, in particular least developed countries, including through the Enhanced Integrated Framework for Trade-Related Technical Assistance to Least Developed Countries', '2025-08-01 08:56:51.210475+00', '2025-08-01 08:56:51.210475+00'),
('91b2c482-204e-4b70-bf44-9acca8a9e543', 8, '8.b', 'Youth employment strategy', 'By 2020, develop and operationalize a global strategy for youth employment and implement the Global Jobs Pact of the International Labour Organization', '2025-08-01 08:56:51.243726+00', '2025-08-01 08:56:51.243726+00'),
('41c06013-480d-4267-a608-4aa832d05a93', 9, '9.1', 'Develop resilient infrastructure', 'Develop quality, reliable, sustainable and resilient infrastructure, including regional and transborder infrastructure, to support economic development and human well-being, with a focus on affordable and equitable access for all', '2025-08-01 08:56:51.27668+00', '2025-08-01 08:56:51.27668+00'),
('c598c0ae-bde8-4e83-9f6b-0d04071c4b0c', 9, '9.2', 'Promote inclusive industrialization', 'Promote inclusive and sustainable industrialization and, by 2030, significantly raise industry''s share of employment and gross domestic product, in line with national circumstances, and double its share in least developed countries', '2025-08-01 08:56:51.309941+00', '2025-08-01 08:56:51.309941+00'),
('fcbfca5f-2a1f-42c8-a377-0e502a67a967', 9, '9.3', 'Increase access to financial services', 'Increase the access of small-scale industrial and other enterprises, in particular in developing countries, to financial services, including affordable credit, and their integration into value chains and markets', '2025-08-01 08:56:51.342924+00', '2025-08-01 08:56:51.342924+00'),
('f94c3ba2-ac49-4b85-ac96-5b10bec11f8d', 9, '9.4', 'Upgrade infrastructure for sustainability', 'By 2030, upgrade infrastructure and retrofit industries to make them sustainable, with increased resource-use efficiency and greater adoption of clean and environmentally sound technologies and industrial processes, with all countries taking action in accordance with their respective capabilities', '2025-08-01 08:56:51.376779+00', '2025-08-01 08:56:51.376779+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('fe83ed3b-b8e8-4181-b8fc-cb892bfa8a79', 9, '9.5', 'Enhance scientific research', 'Enhance scientific research, upgrade the technological capabilities of industrial sectors in all countries, in particular developing countries, including, by 2030, encouraging innovation and substantially increasing the number of research and development workers per 1 million people and public and private research and development spending', '2025-08-01 08:56:51.409852+00', '2025-08-01 08:56:51.409852+00'),
('f0c6a195-a410-458c-b16c-a8f3d054cebb', 9, '9.a', 'Facilitate sustainable infrastructure', 'Facilitate sustainable and resilient infrastructure development in developing countries through enhanced financial, technological and technical support to African countries, least developed countries, landlocked developing countries and small island developing States', '2025-08-01 08:56:51.442927+00', '2025-08-01 08:56:51.442927+00'),
('9c0aefd9-ce78-4c0a-8033-c76ad5867b9e', 9, '9.b', 'Support technology development', 'Support domestic technology development, research and innovation in developing countries, including by ensuring a conducive policy environment for, inter alia, industrial diversification and value addition to commodities', '2025-08-01 08:56:51.476135+00', '2025-08-01 08:56:51.476135+00'),
('3090da29-4dd4-4cbd-94cf-bc52dacaa589', 9, '9.c', 'Increase access to ICT', 'Significantly increase access to information and communications technology and strive to provide universal and affordable access to the Internet in least developed countries by 2020', '2025-08-01 08:56:51.509061+00', '2025-08-01 08:56:51.509061+00'),
('83186fe4-1b15-451b-a089-feccb8cf8eaf', 10, '10.1', 'Reduce income inequalities', 'By 2030, progressively achieve and sustain income growth of the bottom 40 per cent of the population at a rate higher than the national average', '2025-08-01 08:56:51.542073+00', '2025-08-01 08:56:51.542073+00'),
('fb6e15a3-c903-4e27-bd83-6092ac78a3f0', 10, '10.2', 'Promote universal social inclusion', 'By 2030, empower and promote the social, economic and political inclusion of all, irrespective of age, sex, disability, race, ethnicity, origin, religion or economic or other status', '2025-08-01 08:56:51.575038+00', '2025-08-01 08:56:51.575038+00'),
('9f676d59-7052-4922-b3ef-f5dc2586b151', 10, '10.3', 'Ensure equal opportunity', 'Ensure equal opportunity and reduce inequalities of outcome, including by eliminating discriminatory laws, policies and practices and promoting appropriate legislation, policies and action in this regard', '2025-08-01 08:56:51.607991+00', '2025-08-01 08:56:51.607991+00'),
('b1cc21b8-1a37-48ff-a13d-23aee8653016', 10, '10.4', 'Adopt fiscal and social policies', 'Adopt policies, especially fiscal, wage and social protection policies, and progressively achieve greater equality', '2025-08-01 08:56:51.641137+00', '2025-08-01 08:56:51.641137+00'),
('055b42ec-fe9b-4ebd-b677-e317bcfe3bb6', 10, '10.5', 'Improve regulation of financial markets', 'Improve the regulation and monitoring of global financial markets and institutions and strengthen the implementation of such regulations', '2025-08-01 08:56:51.674059+00', '2025-08-01 08:56:51.674059+00'),
('75910455-8126-4327-883a-93d2bda3b6ad', 10, '10.6', 'Enhanced representation for developing countries', 'Ensure enhanced representation and voice for developing countries in decision-making in global international economic and financial institutions in order to deliver more effective, credible, accountable and legitimate institutions', '2025-08-01 08:56:51.707065+00', '2025-08-01 08:56:51.707065+00'),
('8a2a1533-acfc-4989-95cc-a65935c6d198', 10, '10.7', 'Facilitate safe migration', 'Facilitate orderly, safe, regular and responsible migration and mobility of people, including through the implementation of planned and well-managed migration policies', '2025-08-01 08:56:51.740199+00', '2025-08-01 08:56:51.740199+00'),
('6b067fec-c626-4d16-b1b5-5de49697c7cd', 10, '10.a', 'Special treatment for least developed countries', 'Implement the principle of special and differential treatment for developing countries, in particular least developed countries, in accordance with World Trade Organization agreements', '2025-08-01 08:56:51.773191+00', '2025-08-01 08:56:51.773191+00'),
('a16e6f0d-d097-45e3-8f75-8501548a4d93', 10, '10.b', 'Encourage development assistance', 'Encourage official development assistance and financial flows, including foreign direct investment, to States where the need is greatest, in particular least developed countries, African countries, small island developing States and landlocked developing countries, in accordance with their national plans and programmes', '2025-08-01 08:56:51.806231+00', '2025-08-01 08:56:51.806231+00'),
('609c6843-479a-4363-a320-993218971d61', 10, '10.c', 'Reduce transaction costs of remittances', 'By 2030, reduce to less than 3 per cent the transaction costs of migrant remittances and eliminate remittance corridors with costs higher than 5 per cent', '2025-08-01 08:56:51.839363+00', '2025-08-01 08:56:51.839363+00'),
('531fe476-f2fc-431d-9f3f-33600b9b1908', 11, '11.1', 'Safe and affordable housing', 'By 2030, ensure access for all to adequate, safe and affordable housing and basic services and upgrade slums', '2025-08-01 08:56:51.872523+00', '2025-08-01 08:56:51.872523+00'),
('30cc003e-06f9-4984-8dcd-ac22f0246252', 11, '11.2', 'Sustainable transport systems', 'By 2030, provide access to safe, affordable, accessible and sustainable transport systems for all, improving road safety, notably by expanding public transport, with special attention to the needs of those in vulnerable situations, women, children, persons with disabilities and older persons', '2025-08-01 08:56:51.904421+00', '2025-08-01 08:56:51.904421+00'),
('dcc7991e-acff-4e26-8626-2ac02a3d48b9', 11, '11.3', 'Inclusive urbanization', 'By 2030, enhance inclusive and sustainable urbanization and capacity for participatory, integrated and sustainable human settlement planning and management in all countries', '2025-08-01 08:56:51.937586+00', '2025-08-01 08:56:51.937586+00'),
('4cf49bb5-d2cb-46d0-a499-e001e59e0428', 11, '11.4', 'Protect cultural heritage', 'Strengthen efforts to protect and safeguard the world''s cultural and natural heritage', '2025-08-01 08:56:51.970771+00', '2025-08-01 08:56:51.970771+00'),
('f4224163-8c13-4394-9541-a9aa8c2de41a', 11, '11.5', 'Reduce disaster losses', 'By 2030, significantly reduce the number of deaths and the number of people affected and substantially decrease the direct economic losses relative to global gross domestic product caused by disasters, including water-related disasters, with a focus on protecting the poor and people in vulnerable situations', '2025-08-01 08:56:52.003879+00', '2025-08-01 08:56:52.003879+00'),
('8b999434-cffd-4648-ae85-407d537a94ac', 11, '11.6', 'Reduce environmental impact of cities', 'By 2030, reduce the adverse per capita environmental impact of cities, including by paying special attention to air quality and municipal and other waste management', '2025-08-01 08:56:52.036861+00', '2025-08-01 08:56:52.036861+00'),
('2ac7d610-c92c-4d99-b3fc-b1869f9e3fdd', 11, '11.7', 'Provide access to safe spaces', 'By 2030, provide universal access to safe, inclusive and accessible, green and public spaces, in particular for women and children, older persons and persons with disabilities', '2025-08-01 08:56:52.07074+00', '2025-08-01 08:56:52.07074+00'),
('97cd86a4-7409-4042-a82b-2a861b296b41', 11, '11.a', 'Support positive urban-rural linkages', 'Support positive economic, social and environmental links between urban, peri-urban and rural areas by strengthening national and regional development planning', '2025-08-01 08:56:52.104124+00', '2025-08-01 08:56:52.104124+00'),
('1b2569c6-f7b5-4433-b800-028f9cede8b4', 11, '11.b', 'Implement integrated policies', 'By 2020, substantially increase the number of cities and human settlements adopting and implementing integrated policies and plans towards inclusion, resource efficiency, mitigation and adaptation to climate change, resilience to disasters, and develop and implement, in line with the Sendai Framework for Disaster Risk Reduction 2015-2030, holistic disaster risk management at all levels', '2025-08-01 08:56:52.137028+00', '2025-08-01 08:56:52.137028+00'),
('c1ca777d-779f-4d16-a0aa-0fc906abf219', 11, '11.c', 'Support least developed countries', 'Support least developed countries, including through financial and technical assistance, in building sustainable and resilient buildings utilizing local materials', '2025-08-01 08:56:52.170134+00', '2025-08-01 08:56:52.170134+00'),
('a49d24a2-a06b-4afc-a3f4-826f6058f7c1', 12, '12.1', 'Implement sustainable consumption patterns', 'Implement the 10-year framework of programmes on sustainable consumption and production, all countries taking action, with developed countries taking the lead, taking into account the development and capabilities of developing countries', '2025-08-01 08:56:52.203165+00', '2025-08-01 08:56:52.203165+00'),
('a723f1c3-b008-4d80-af29-b8032a414230', 12, '12.2', 'Sustainable management of natural resources', 'By 2030, achieve the sustainable management and efficient use of natural resources', '2025-08-01 08:56:52.236335+00', '2025-08-01 08:56:52.236335+00'),
('2d5d3d8f-0ddf-4102-b90b-fbd3b0329e2d', 12, '12.3', 'Halve food waste', 'By 2030, halve per capita global food waste at the retail and consumer levels and reduce food losses along production and supply chains, including post-harvest losses', '2025-08-01 08:56:52.269268+00', '2025-08-01 08:56:52.269268+00'),
('d21963f5-7c9b-4848-ba0e-75125446037c', 12, '12.4', 'Responsible management of chemicals', 'By 2020, achieve the environmentally sound management of chemicals and all wastes throughout their life cycle, in accordance with agreed international frameworks, and significantly reduce their release to air, water and soil in order to minimize their adverse impacts on human health and the environment', '2025-08-01 08:56:52.306651+00', '2025-08-01 08:56:52.306651+00'),
('6330b62b-86a8-4dcd-a35b-f79fc73e199c', 12, '12.5', 'Reduce waste generation', 'By 2030, substantially reduce waste generation through prevention, reduction, recycling and reuse', '2025-08-01 08:56:52.339776+00', '2025-08-01 08:56:52.339776+00'),
('32061906-0a2c-40b2-99ab-0255489f5cec', 12, '12.6', 'Encourage sustainable practices', 'Encourage companies, especially large and transnational companies, to adopt sustainable practices and to integrate sustainability information into their reporting cycle', '2025-08-01 08:56:52.372866+00', '2025-08-01 08:56:52.372866+00'),
('def0e144-93cc-417e-9efc-48d23e715aee', 12, '12.7', 'Promote sustainable procurement', 'Promote public procurement practices that are sustainable, in accordance with national policies and priorities', '2025-08-01 08:56:52.405884+00', '2025-08-01 08:56:52.405884+00'),
('5e5c22d3-e6c6-49d4-987e-ffbb07d665ec', 12, '12.8', 'Promote awareness of sustainable development', 'By 2030, ensure that people everywhere have the relevant information and awareness for sustainable development and lifestyles in harmony with nature', '2025-08-01 08:56:52.439284+00', '2025-08-01 08:56:52.439284+00'),
('ff451639-562f-4644-b24b-1fbed81d0da8', 12, '12.a', 'Support developing countries'' scientific capacity', 'Support developing countries to strengthen their scientific and technological capacity to move towards more sustainable patterns of consumption and production', '2025-08-01 08:56:52.472778+00', '2025-08-01 08:56:52.472778+00'),
('df583139-bdf8-4282-a927-d65401616a73', 12, '12.b', 'Develop sustainable tourism tools', 'Develop and implement tools to monitor sustainable development impacts for sustainable tourism that creates jobs and promotes local culture and products', '2025-08-01 08:56:52.505818+00', '2025-08-01 08:56:52.505818+00'),
('fb2da083-be09-440e-9282-670f298ef54a', 12, '12.c', 'Remove market distortions in fossil fuels', 'Rationalize inefficient fossil-fuel subsidies that encourage wasteful consumption by removing market distortions, in accordance with national circumstances, including by restructuring taxation and phasing out those harmful subsidies, where they exist, to reflect their environmental impacts, taking fully into account the specific needs and conditions of developing countries and minimizing the possible adverse impacts on their development in a manner that protects the poor and the affected communities', '2025-08-01 08:56:52.541228+00', '2025-08-01 08:56:52.541228+00'),
('e386df06-8ca1-49e3-a82b-832a6ee58d6f', 13, '13.1', 'Strengthen resilience to climate hazards', 'Strengthen resilience and adaptive capacity to climate-related hazards and natural disasters in all countries', '2025-08-01 08:56:52.574274+00', '2025-08-01 08:56:52.574274+00'),
('7cc43704-572b-42d7-8ae6-a4285122ef41', 13, '13.2', 'Integrate climate measures', 'Integrate climate change measures into national policies, strategies and planning', '2025-08-01 08:56:52.607647+00', '2025-08-01 08:56:52.607647+00'),
('2e12a927-eebe-4a41-89e9-58c7ad2304a4', 13, '13.3', 'Improve education on climate change', 'Improve education, awareness-raising and human and institutional capacity on climate change mitigation, adaptation, impact reduction and early warning', '2025-08-01 08:56:52.640294+00', '2025-08-01 08:56:52.640294+00'),
('6fe268e8-2201-4e48-a248-a85e0d4e876c', 13, '13.a', 'Implement climate finance commitment', 'Implement the commitment undertaken by developed-country parties to the United Nations Framework Convention on Climate Change to a goal of mobilizing jointly $100 billion annually by 2020 from all sources to address the needs of developing countries in the context of meaningful mitigation actions and transparency on implementation and fully operationalize the Green Climate Fund through its capitalization as soon as possible', '2025-08-01 08:56:52.67323+00', '2025-08-01 08:56:52.67323+00'),
('40b17fe2-1a3f-4592-829e-facc3c95dc00', 13, '13.b', 'Promote mechanisms for climate planning', 'Promote mechanisms for raising capacity for effective climate change-related planning and management in least developed countries and small island developing States, including focusing on women, youth and local and marginalized communities', '2025-08-01 08:56:52.706101+00', '2025-08-01 08:56:52.706101+00'),
('3c4d7bd6-47a9-4fbd-b914-2792d6ba42a4', 14, '14.1', 'Reduce marine pollution', 'By 2025, prevent and significantly reduce marine pollution of all kinds, in particular from land-based activities, including marine debris and nutrient pollution', '2025-08-01 08:56:52.739101+00', '2025-08-01 08:56:52.739101+00'),
('0abd9264-a20f-4af1-be61-d00c180c04b4', 14, '14.2', 'Protect marine ecosystems', 'By 2020, sustainably manage and protect marine and coastal ecosystems to avoid significant adverse impacts, including by strengthening their resilience, and take action for their restoration in order to achieve healthy and productive oceans', '2025-08-01 08:56:52.772061+00', '2025-08-01 08:56:52.772061+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('7500afb8-979d-4e6d-92b5-953045857b99', 14, '14.3', 'Minimize ocean acidification', 'Minimize and address the impacts of ocean acidification, including through enhanced scientific cooperation at all levels', '2025-08-01 08:56:52.805184+00', '2025-08-01 08:56:52.805184+00'),
('12642006-cff3-46aa-8633-15154ac704fd', 14, '14.4', 'Regulate fishing', 'By 2020, effectively regulate harvesting and end overfishing, illegal, unreported and unregulated fishing and destructive fishing practices and implement science-based management plans, in order to restore fish stocks in the shortest time feasible, at least to levels that can produce maximum sustainable yield as determined by their biological characteristics', '2025-08-01 08:56:52.837349+00', '2025-08-01 08:56:52.837349+00'),
('7e59a854-7de7-4a99-8ba8-fcdc20875e83', 14, '14.5', 'Conserve marine areas', 'By 2020, conserve at least 10 per cent of coastal and marine areas, consistent with national and international law and based on the best available scientific information', '2025-08-01 08:56:52.870211+00', '2025-08-01 08:56:52.870211+00'),
('93ed8e5a-23db-4a3a-8233-88b785e9e407', 14, '14.6', 'End subsidies contributing to overfishing', 'By 2020, prohibit certain forms of fisheries subsidies which contribute to overcapacity and overfishing, eliminate subsidies that contribute to illegal, unreported and unregulated fishing and refrain from introducing new such subsidies, recognizing that appropriate and effective special and differential treatment for developing and least developed countries should be an integral part of the World Trade Organization fisheries subsidies negotiation', '2025-08-01 08:56:52.903471+00', '2025-08-01 08:56:52.903471+00'),
('b76d4c22-853a-494d-ba24-97a0b8d47c72', 14, '14.7', 'Increase economic benefits from marine resources', 'By 2030, increase the economic benefits to Small Island developing States and least developed countries from the sustainable use of marine resources, including through sustainable management of fisheries, aquaculture and tourism', '2025-08-01 08:56:52.935442+00', '2025-08-01 08:56:52.935442+00'),
('f9ce70a8-2ebf-4441-aed0-ee8979576f8f', 14, '14.a', 'Increase scientific knowledge', 'Increase scientific knowledge, develop research capacity and transfer marine technology, taking into account the Intergovernmental Oceanographic Commission Criteria and Guidelines on the Transfer of Marine Technology, in order to improve ocean health and to enhance the contribution of marine biodiversity to the development of developing countries, in particular small island developing States and least developed countries', '2025-08-01 08:56:52.967256+00', '2025-08-01 08:56:52.967256+00'),
('dd679cc1-4401-4e43-b51f-33cd48912ebb', 14, '14.b', 'Provide access for small-scale fishers', 'Provide access for small-scale artisanal fishers to marine resources and markets', '2025-08-01 08:56:53.000305+00', '2025-08-01 08:56:53.000305+00'),
('6d2ee004-bb61-45e3-b476-de9f8af44151', 14, '14.c', 'Enhance conservation through international law', 'Enhance the conservation and sustainable use of oceans and their resources by implementing international law as reflected in UNCLOS, which provides the legal framework for the conservation and sustainable use of oceans and their resources, as recalled in paragraph 158 of The Future We Want', '2025-08-01 08:56:53.033699+00', '2025-08-01 08:56:53.033699+00'),
('6d78e750-3ade-4e92-a424-c3079595c43d', 15, '15.1', 'Conserve terrestrial ecosystems', 'By 2020, ensure the conservation, restoration and sustainable use of terrestrial and inland freshwater ecosystems and their services, in particular forests, wetlands, mountains and drylands, in line with obligations under international agreements', '2025-08-01 08:56:53.067207+00', '2025-08-01 08:56:53.067207+00'),
('3eb8817b-f6ea-4b59-b1b4-866400e1a858', 15, '15.2', 'End deforestation', 'By 2020, promote the implementation of sustainable management of all types of forests, halt deforestation, restore degraded forests and substantially increase afforestation and reforestation globally', '2025-08-01 08:56:53.100432+00', '2025-08-01 08:56:53.100432+00'),
('7f68fd5c-3eef-4969-9294-2ad52c0911b4', 15, '15.3', 'Combat desertification', 'By 2030, combat desertification, restore degraded land and soil, including land affected by desertification, drought and floods, and strive to achieve a land degradation-neutral world', '2025-08-01 08:56:53.132359+00', '2025-08-01 08:56:53.132359+00'),
('3b2d7a28-11c0-4adc-8680-661a4177829e', 15, '15.4', 'Conserve mountain ecosystems', 'By 2030, ensure the conservation of mountain ecosystems, including their biodiversity, in order to enhance their capacity to provide benefits that are essential for sustainable development', '2025-08-01 08:56:53.164392+00', '2025-08-01 08:56:53.164392+00'),
('5d276d69-6abd-427e-b841-c94c86272cd4', 15, '15.5', 'Protect biodiversity', 'Take urgent and significant action to reduce the degradation of natural habitats, halt the loss of biodiversity and, by 2020, protect and prevent the extinction of threatened species', '2025-08-01 08:56:53.197997+00', '2025-08-01 08:56:53.197997+00'),
('7eed5fb9-325c-4922-87a6-bf592f6e4674', 15, '15.6', 'Promote access to genetic resources', 'Promote fair and equitable sharing of the benefits arising from the utilization of genetic resources and promote appropriate access to such resources, as internationally agreed', '2025-08-01 08:56:53.231356+00', '2025-08-01 08:56:53.231356+00'),
('d6e5690c-d8d5-4920-86d4-4fefd822fbb5', 15, '15.7', 'End poaching and trafficking', 'Take urgent action to end poaching and trafficking of protected species of flora and fauna and address both demand and supply of illegal wildlife products', '2025-08-01 08:56:53.264473+00', '2025-08-01 08:56:53.264473+00'),
('f4f862d0-f5f5-4c4c-b296-9e872ae29d5e', 15, '15.8', 'Prevent invasive species', 'By 2020, introduce measures to prevent the introduction and significantly reduce the impact of invasive alien species on land and water ecosystems and control or eradicate the priority species', '2025-08-01 08:56:53.298447+00', '2025-08-01 08:56:53.298447+00'),
('105c4dc6-a7e0-49aa-a697-cbb067735e90', 15, '15.9', 'Integrate ecosystem values', 'By 2020, integrate ecosystem and biodiversity values into national and local planning, development processes, poverty reduction strategies and accounts', '2025-08-01 08:56:53.346495+00', '2025-08-01 08:56:53.346495+00'),
('bf84591e-34e5-4434-ba41-4db36920cc81', 15, '15.a', 'Mobilize resources for biodiversity', 'Mobilize and significantly increase financial resources from all sources to conserve and sustainably use biodiversity and ecosystems', '2025-08-01 08:56:53.380369+00', '2025-08-01 08:56:53.380369+00'),
('c36e3020-fdf4-4bb5-b6a6-1289a51e048b', 15, '15.b', 'Finance sustainable forest management', 'Mobilize significant resources from all sources and at all levels to finance sustainable forest management and provide adequate incentives to developing countries to advance such management, including for conservation and reforestation', '2025-08-01 08:56:53.414735+00', '2025-08-01 08:56:53.414735+00'),
('1fee30a2-386a-4611-a08a-8c89c2bf480d', 3, '3.3', 'Target 3.3', 'Target 3.3 description', '2025-08-01 10:03:15.166465+00', '2025-08-01 10:03:15.166465+00'),
('8f053a96-1680-459b-91fc-9227b5b06acf', 15, '15.c', 'Combat poaching and trafficking', 'Enhance global support for efforts to combat poaching and trafficking of protected species, including by increasing the capacity of local communities to pursue sustainable livelihood opportunities', '2025-08-01 08:56:53.457051+00', '2025-08-01 08:56:53.457051+00'),
('a89d4ff6-fc49-4b1b-8659-394e93a0c37a', 16, '16.1', 'Reduce violence', 'Significantly reduce all forms of violence and related death rates everywhere', '2025-08-01 08:56:53.494644+00', '2025-08-01 08:56:53.494644+00'),
('c6e91684-bf6b-4de2-b285-a2e12d1919c8', 16, '16.2', 'End abuse and trafficking of children', 'End abuse, exploitation, trafficking and all forms of violence against and torture of children', '2025-08-01 08:56:53.528225+00', '2025-08-01 08:56:53.528225+00'),
('7f566432-2886-4a54-963e-3b4b5ece1cf6', 16, '16.3', 'Promote rule of law', 'Promote the rule of law at the national and international levels and ensure equal access to justice for all', '2025-08-01 08:56:53.5616+00', '2025-08-01 08:56:53.5616+00'),
('9f8dd811-6dfa-4eb3-a7cb-a7c8fbb25016', 16, '16.4', 'Combat illicit financial flows', 'By 2030, significantly reduce illicit financial and arms flows, strengthen the recovery and return of stolen assets and combat all forms of organized crime', '2025-08-01 08:56:53.594707+00', '2025-08-01 08:56:53.594707+00'),
('60d01142-aa6f-479e-ad97-d324c647a973', 16, '16.5', 'Reduce corruption and bribery', 'Substantially reduce corruption and bribery in all their forms', '2025-08-01 08:56:53.646092+00', '2025-08-01 08:56:53.646092+00'),
('3eba8b2b-eec5-4363-bd00-b7796fe09e33', 16, '16.6', 'Develop effective institutions', 'Develop effective, accountable and transparent institutions at all levels', '2025-08-01 08:56:53.680285+00', '2025-08-01 08:56:53.680285+00'),
('791eea07-c04d-47a4-a814-3012e0b42d04', 16, '16.7', 'Ensure responsive decision-making', 'Ensure responsive, inclusive, participatory and representative decision-making at all levels', '2025-08-01 08:56:53.713416+00', '2025-08-01 08:56:53.713416+00'),
('040251bb-e41a-499c-8a71-48fec2ed4e24', 16, '16.8', 'Broaden participation in global governance', 'Broaden and strengthen the participation of developing countries in the institutions of global governance', '2025-08-01 08:56:53.749546+00', '2025-08-01 08:56:53.749546+00'),
('d69fb808-f54b-4315-bcb5-66238e4de12f', 16, '16.9', 'Legal identity for all', 'By 2030, provide legal identity for all, including birth registration', '2025-08-01 08:56:53.782646+00', '2025-08-01 08:56:53.782646+00'),
('14042349-ecb2-4881-b9b6-0a4b2f2a6c7b', 16, '16.10', 'Ensure public access to information', 'Ensure public access to information and protect fundamental freedoms, in accordance with national legislation and international agreements', '2025-08-01 08:56:53.818006+00', '2025-08-01 08:56:53.818006+00'),
('96fbb2b4-9941-40be-9eaa-83bf66ae31b5', 16, '16.a', 'Strengthen institutions to prevent violence', 'Strengthen relevant national institutions, including through international cooperation, for building capacity at all levels, in particular in developing countries, to prevent violence and combat terrorism and crime', '2025-08-01 08:56:53.851184+00', '2025-08-01 08:56:53.851184+00'),
('2327cf7e-f11c-4150-abd8-1dab84561221', 16, '16.b', 'Promote non-discriminatory laws', 'Promote and enforce non-discriminatory laws and policies for sustainable development', '2025-08-01 08:56:53.883391+00', '2025-08-01 08:56:53.883391+00'),
('9e204f3c-cd6f-40ad-b6b0-547b5c5cf063', 17, '17.1', 'Strengthen domestic revenue collection', 'Strengthen domestic resource mobilization, including through international support to developing countries, to improve domestic capacity for tax and other revenue collection', '2025-08-01 08:56:53.917491+00', '2025-08-01 08:56:53.917491+00'),
('0ab63236-c8e0-4e5f-86cc-d758f5dab1ca', 17, '17.2', 'Implement ODA commitments', 'Developed countries to implement fully their official development assistance commitments, including the commitment by many developed countries to achieve the target of 0.7 per cent of gross national income for official development assistance (ODA/GNI) to developing countries and 0.15 to 0.20 per cent of ODA/GNI to least developed countries', '2025-08-01 08:56:53.950583+00', '2025-08-01 08:56:53.950583+00'),
('25417c21-3f9d-4ae5-a48b-d57db76d29ff', 17, '17.3', 'Mobilize financial resources', 'Mobilize additional financial resources for developing countries from multiple sources', '2025-08-01 08:56:53.983748+00', '2025-08-01 08:56:53.983748+00'),
('dc6815c7-cc0d-4e54-aac5-99a5513a7072', 17, '17.4', 'Assist developing countries in debt sustainability', 'Assist developing countries in attaining long-term debt sustainability through coordinated policies aimed at fostering debt financing, debt relief and debt restructuring, as appropriate, and address the external debt of highly indebted poor countries to reduce debt distress', '2025-08-01 08:56:54.017157+00', '2025-08-01 08:56:54.017157+00'),
('429e41d5-18c5-46ba-98d8-59d0dda2e615', 17, '17.5', 'Adopt investment promotion regimes', 'Adopt and implement investment promotion regimes for least developed countries', '2025-08-01 08:56:54.051575+00', '2025-08-01 08:56:54.051575+00'),
('50db0b53-e09e-4d75-a230-606d31fbc5c7', 17, '17.6', 'Enhance cooperation on science and technology', 'Enhance North-South, South-South and triangular regional and international cooperation on and access to science, technology and innovation and enhance knowledge sharing on mutually agreed terms, including through improved coordination among existing mechanisms, in particular at the United Nations level, and through a global technology facilitation mechanism', '2025-08-01 08:56:54.085051+00', '2025-08-01 08:56:54.085051+00'),
('f8b4439b-fc8d-4dc1-bd66-3061ac63c011', 17, '17.7', 'Promote sustainable technologies', 'Promote the development, transfer, dissemination and diffusion of environmentally sound technologies to developing countries on favourable terms, including on concessional and preferential terms, as mutually agreed', '2025-08-01 08:56:54.118156+00', '2025-08-01 08:56:54.118156+00'),
('ce3feb69-14ee-466f-a653-57c776b72ce4', 17, '17.8', 'Operationalize technology bank', 'Fully operationalize the technology bank and science, technology and innovation capacity-building mechanism for least developed countries by 2017 and enhance the use of enabling technology, in particular information and communications technology', '2025-08-01 08:56:54.151303+00', '2025-08-01 08:56:54.151303+00'),
('13ae081f-21ac-4a19-8bc9-a062306c38fc', 17, '17.9', 'Enhance capacity-building support', 'Enhance international support for implementing effective and targeted capacity-building in developing countries to support national plans to implement all the sustainable development goals, including through North-South, South-South and triangular cooperation', '2025-08-01 08:56:54.184712+00', '2025-08-01 08:56:54.184712+00'),
('63ec4d3b-341e-45f3-bebb-5d5de0d0a1fb', 17, '17.10', 'Promote universal trading system', 'Promote a universal, rules-based, open, non-discriminatory and equitable multilateral trading system under the World Trade Organization, including through the conclusion of negotiations under its Doha Development Agenda', '2025-08-01 08:56:54.21821+00', '2025-08-01 08:56:54.21821+00'),
('add8cbfd-0697-4e38-ac4e-15fd5690bba5', 17, '17.11', 'Increase developing country exports', 'Significantly increase the exports of developing countries, in particular with a view to doubling the least developed countries'' share of global exports by 2020', '2025-08-01 08:56:54.251456+00', '2025-08-01 08:56:54.251456+00'),
('490f4857-4eea-430b-848f-5559740560c5', 17, '17.12', 'Realize timely duty-free access', 'Realize timely implementation of duty-free and quota-free market access on a lasting basis for all least developed countries, consistent with World Trade Organization decisions, including by ensuring that preferential rules of origin applicable to imports from least developed countries are transparent and simple, and contribute to facilitating market access', '2025-08-01 08:56:54.28466+00', '2025-08-01 08:56:54.28466+00'),
('7d1991d7-3f0e-4c72-b609-1cd32796ba64', 17, '17.13', 'Enhance macroeconomic stability', 'Enhance global macroeconomic stability, including through policy coordination and policy coherence', '2025-08-01 08:56:54.317884+00', '2025-08-01 08:56:54.317884+00'),
('bae0cd30-be7c-4b91-912c-9e5e0aaa62ee', 17, '17.14', 'Enhance policy coherence', 'Enhance policy coherence for sustainable development', '2025-08-01 08:56:54.35086+00', '2025-08-01 08:56:54.35086+00'),
('34032812-ba32-4d1a-8ce5-b33233b5ca10', 17, '17.15', 'Respect policy space', 'Respect each country''s policy space and leadership to establish and implement policies for poverty eradication and sustainable development', '2025-08-01 08:56:54.384319+00', '2025-08-01 08:56:54.384319+00'),
('a801580e-52a4-456c-abc7-5d8f55d153b3', 17, '17.16', 'Enhance global partnership', 'Enhance the global partnership for sustainable development, complemented by multi-stakeholder partnerships that mobilize and share knowledge, expertise, technology and financial resources, to support the achievement of the sustainable development goals in all countries, in particular developing countries', '2025-08-01 08:56:54.417451+00', '2025-08-01 08:56:54.417451+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('d88a0c3c-129a-4621-9a0a-c12faf693bf3', 3, '3.3', 'Target 3.3', 'Target 3.3 description', '2025-08-01 10:03:15.199959+00', '2025-08-01 10:03:15.199959+00'),
('cf93433b-d442-4b50-a693-f15a7bed2afd', 17, '17.17', 'Encourage effective partnerships', 'Encourage and promote effective public, public-private and civil society partnerships, building on the experience and resourcing strategies of partnerships', '2025-08-01 08:56:54.450868+00', '2025-08-01 08:56:54.450868+00'),
('0c6697ba-5e25-41eb-a1ba-bc647f20cadc', 17, '17.18', 'Enhance data availability', 'By 2020, enhance capacity-building support to developing countries, including for least developed countries and small island developing States, to increase significantly the availability of high-quality, timely and reliable data disaggregated by income, gender, age, race, ethnicity, migratory status, disability, geographic location and other characteristics relevant in national contexts', '2025-08-01 08:56:54.484675+00', '2025-08-01 08:56:54.484675+00'),
('6ebb508c-64cb-4087-9a8e-ed7553f5d9e0', 17, '17.19', 'Develop broader progress measures', 'By 2030, build on existing initiatives to develop measurements of progress on sustainable development that complement gross domestic product, and support statistical capacity-building in developing countries', '2025-08-01 08:56:54.519102+00', '2025-08-01 08:56:54.519102+00'),
('3aee377d-bf5a-447e-afc5-4256f47b1e4e', 1, '1.1', 'Eradicate extreme poverty', 'By 2030, eradicate extreme poverty for all people everywhere, currently measured as people living on less than $1.25 a day', '2025-08-01 08:58:11.02504+00', '2025-08-01 08:58:11.02504+00'),
('15d4c276-34cc-4066-b886-895ac714dc35', 1, '1.2', 'Reduce poverty by half', 'By 2030, reduce at least by half the proportion of men, women and children of all ages living in poverty in all its dimensions according to national definitions', '2025-08-01 08:58:11.058605+00', '2025-08-01 08:58:11.058605+00'),
('56c7e43c-bd72-408f-93f1-5a3238fae80f', 1, '1.3', 'Social protection systems', 'Implement nationally appropriate social protection systems and measures for all, including floors, and by 2030 achieve substantial coverage of the poor and the vulnerable', '2025-08-01 08:58:11.091439+00', '2025-08-01 08:58:11.091439+00'),
('06128e14-f1d5-4cf8-b4a0-ad2ef701b33d', 1, '1.4', 'Equal access to basic services', 'By 2030, ensure that all men and women, in particular the poor and the vulnerable, have equal rights to economic resources, as well as access to basic services, ownership and control over land and other forms of property, inheritance, natural resources, appropriate new technology and financial services, including microfinance', '2025-08-01 08:58:11.12478+00', '2025-08-01 08:58:11.12478+00'),
('ae2677c7-0c2f-4f1a-9cd7-d60f00b25feb', 1, '1.5', 'Resilience to disasters', 'By 2030, build the resilience of the poor and those in vulnerable situations and reduce their exposure and vulnerability to climate-related extreme events and other economic, social and environmental shocks and disasters', '2025-08-01 08:58:11.157967+00', '2025-08-01 08:58:11.157967+00'),
('dd925281-14c4-4239-83ce-5686ba10a314', 1, '1.a', 'Resource mobilization', 'Ensure significant mobilization of resources from a variety of sources, including through enhanced development cooperation, in order to provide adequate and predictable means for developing countries, in particular least developed countries, to implement programmes and policies to end poverty in all its dimensions', '2025-08-01 08:58:11.190733+00', '2025-08-01 08:58:11.190733+00'),
('8fdf9169-c994-4ffa-9837-21a9a6ece69e', 1, '1.b', 'Policy frameworks', 'Create sound policy frameworks at the national, regional and international levels, based on pro-poor and gender-sensitive development strategies, to support accelerated investment in poverty eradication actions', '2025-08-01 08:58:11.224358+00', '2025-08-01 08:58:11.224358+00'),
('9fe3b3f9-1a4f-4de1-9417-0874896e8bbd', 2, '2.1', 'End hunger', 'By 2030, end hunger and ensure access by all people, in particular the poor and people in vulnerable situations, including infants, to safe, nutritious and sufficient food all year round', '2025-08-01 08:58:11.26021+00', '2025-08-01 08:58:11.26021+00'),
('c4634557-5644-4ae8-9083-de6856748d65', 2, '2.2', 'End malnutrition', 'By 2030, end all forms of malnutrition, including achieving, by 2025, the internationally agreed targets on stunting and wasting in children under 5 years of age, and address the nutritional needs of adolescent girls, pregnant and lactating women and older persons', '2025-08-01 08:58:11.292991+00', '2025-08-01 08:58:11.292991+00'),
('4e7be924-a398-4b24-ad29-64bdc812c30f', 2, '2.3', 'Double agricultural productivity', 'By 2030, double the agricultural productivity and incomes of small-scale food producers, in particular women, indigenous peoples, family farmers, pastoralists and fishers, including through secure and equal access to land, other productive resources and inputs, knowledge, financial services, markets and opportunities for value addition and non-farm employment', '2025-08-01 08:58:11.326166+00', '2025-08-01 08:58:11.326166+00'),
('220c9315-b5e4-455d-8462-99772e9a1581', 2, '2.4', 'Sustainable food production', 'By 2030, ensure sustainable food production systems and implement resilient agricultural practices that increase productivity and production, that help maintain ecosystems, that strengthen capacity for adaptation to climate change, extreme weather, drought, flooding and other disasters and that progressively improve land and soil quality', '2025-08-01 08:58:11.358907+00', '2025-08-01 08:58:11.358907+00'),
('d93af708-1429-4df6-9b86-5f558ea627b7', 2, '2.5', 'Maintain genetic diversity', 'By 2020, maintain the genetic diversity of seeds, cultivated plants and farmed and domesticated animals and their related wild species, including through soundly managed and diversified seed and plant banks at the national, regional and international levels, and promote access to and fair and equitable sharing of benefits arising from the utilization of genetic resources and associated traditional knowledge, as internationally agreed', '2025-08-01 08:58:11.391521+00', '2025-08-01 08:58:11.391521+00'),
('65511cd8-ac0e-44cb-9c1b-64a2a25b9a6c', 2, '2.a', 'Increase agricultural investment', 'Increase investment, including through enhanced international cooperation, in rural infrastructure, agricultural research and extension services, technology development and plant and livestock gene banks in order to enhance agricultural productive capacity in developing countries, in particular least developed countries', '2025-08-01 08:58:11.422925+00', '2025-08-01 08:58:11.422925+00'),
('bbaa65ed-7291-47fa-9af7-224434e11b93', 2, '2.b', 'Correct trade restrictions', 'Correct and prevent trade restrictions and distortions in world agricultural markets, including through the parallel elimination of all forms of agricultural export subsidies and all export measures with equivalent effect, in accordance with the mandate of the Doha Development Round', '2025-08-01 08:58:11.455737+00', '2025-08-01 08:58:11.455737+00'),
('aff1a6a4-24a7-4d43-a0a2-a8bc5773ac31', 2, '2.c', 'Food commodity markets', 'Adopt measures to ensure the proper functioning of food commodity markets and their derivatives and facilitate timely access to market information, including on food reserves, in order to help limit extreme food price volatility', '2025-08-01 08:58:11.488624+00', '2025-08-01 08:58:11.488624+00'),
('8b400169-1f4d-4cb2-98f8-2b39f60a928f', 3, '3.1', 'Reduce maternal mortality', 'By 2030, reduce the global maternal mortality ratio to less than 70 per 100,000 live births', '2025-08-01 08:58:11.521115+00', '2025-08-01 08:58:11.521115+00'),
('0c9c83e1-ba69-46df-a2cc-201832cd29e2', 3, '3.2', 'End preventable deaths of children', 'By 2030, end preventable deaths of newborns and children under 5 years of age, with all countries aiming to reduce neonatal mortality to at least as low as 12 per 1,000 live births and under5 mortality to at least as low as 25 per 1,000 live births', '2025-08-01 08:58:11.55755+00', '2025-08-01 08:58:11.55755+00'),
('38c386c6-d7be-4165-82c9-2d9f27bd7f30', 3, '3.3', 'End epidemics', 'By 2030, end the epidemics of AIDS, tuberculosis, malaria and neglected tropical diseases and combat hepatitis, water-borne diseases and other communicable diseases', '2025-08-01 08:58:11.590529+00', '2025-08-01 08:58:11.590529+00'),
('f9d5f4bc-3f4e-4af2-b973-a0603d9779dc', 3, '3.4', 'Reduce non-communicable diseases', 'By 2030, reduce by one third premature mortality from non-communicable diseases through prevention and treatment and promote mental health and well-being', '2025-08-01 08:58:11.624881+00', '2025-08-01 08:58:11.624881+00'),
('6b1d0ab6-16c6-4351-a4b7-92c2b030fb20', 3, '3.5', 'Strengthen substance abuse prevention', 'Strengthen the prevention and treatment of substance abuse, including narcotic drug abuse and harmful use of alcohol', '2025-08-01 08:58:11.658139+00', '2025-08-01 08:58:11.658139+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('1efd2b73-df19-4221-b64b-ed9b8a7f5ac5', 3, '3.6', 'Halve road traffic deaths', 'By 2020, halve the number of global deaths and injuries from road traffic accidents', '2025-08-01 08:58:11.69105+00', '2025-08-01 08:58:11.69105+00'),
('ed5ab4d6-5cfe-49eb-b9e8-bed13948a93b', 3, '3.3', 'Target 3.3', 'Target 3.3 description', '2025-08-01 10:03:15.23478+00', '2025-08-01 10:03:15.23478+00'),
('59058fd6-a0a5-4af9-a85e-efe899ba1c33', 3, '3.7', 'Universal reproductive health', 'By 2030, ensure universal access to sexual and reproductive health-care services, including for family planning, information and education, and the integration of reproductive health into national strategies and programmes', '2025-08-01 08:58:11.723199+00', '2025-08-01 08:58:11.723199+00'),
('21346ee3-b24b-4b8f-8f02-0313b1162772', 3, '3.8', 'Universal health coverage', 'Achieve universal health coverage, including financial risk protection, access to quality essential health-care services and access to safe, effective, quality and affordable essential medicines and vaccines for all', '2025-08-01 08:58:11.756218+00', '2025-08-01 08:58:11.756218+00'),
('1c78843a-c1d9-479d-8166-334b869eb908', 3, '3.9', 'Reduce pollution deaths', 'By 2030, substantially reduce the number of deaths and illnesses from hazardous chemicals and air, water and soil pollution and contamination', '2025-08-01 08:58:11.789164+00', '2025-08-01 08:58:11.789164+00'),
('6ac64b48-e243-40c2-a1aa-759ae47c5902', 3, '3.a', 'Tobacco control', 'Strengthen the implementation of the World Health Organization Framework Convention on Tobacco Control in all countries, as appropriate', '2025-08-01 08:58:11.821691+00', '2025-08-01 08:58:11.821691+00'),
('b41fc385-7288-4cfa-8d91-2191d561af89', 3, '3.b', 'Support vaccine research', 'Support the research and development of vaccines and medicines for the communicable and noncommunicable diseases that primarily affect developing countries, provide access to affordable essential medicines and vaccines, in accordance with the Doha Declaration on the TRIPS Agreement and Public Health', '2025-08-01 08:58:11.854173+00', '2025-08-01 08:58:11.854173+00'),
('9d7556f6-dc26-45f7-90db-d78cd5bc932f', 3, '3.c', 'Increase health financing', 'Substantially increase health financing and the recruitment, development, training and retention of the health workforce in developing countries, especially in least developed countries and small island developing States', '2025-08-01 08:58:11.886002+00', '2025-08-01 08:58:11.886002+00'),
('6c5b5b89-aaf5-4449-9709-f52d455791ca', 3, '3.d', 'Strengthen health risk management', 'Strengthen the capacity of all countries, in particular developing countries, for early warning, risk reduction and management of national and global health risks', '2025-08-01 08:58:11.923936+00', '2025-08-01 08:58:11.923936+00'),
('d9446444-4be7-473a-8463-5659f5e93367', 4, '4.1', 'Free primary and secondary education', 'By 2030, ensure that all girls and boys complete free, equitable and quality primary and secondary education leading to relevant and effective learning outcomes', '2025-08-01 08:58:11.956688+00', '2025-08-01 08:58:11.956688+00'),
('a17cb357-d805-42f2-8fa7-7f616c54e883', 4, '4.2', 'Early childhood development', 'By 2030, ensure that all girls and boys have access to quality early childhood development, care and preprimary education so that they are ready for primary education', '2025-08-01 08:58:11.989695+00', '2025-08-01 08:58:11.989695+00'),
('62d43128-68be-466a-8fdb-1fc4c162c71d', 4, '4.3', 'Equal access to technical/vocational education', 'By 2030, ensure equal access for all women and men to affordable and quality technical, vocational and tertiary education, including university', '2025-08-01 08:58:12.025624+00', '2025-08-01 08:58:12.025624+00'),
('b90fcd1e-54cb-480e-9e38-f33def64a735', 4, '4.4', 'Increase relevant skills for employment', 'By 2030, substantially increase the number of youth and adults who have relevant skills, including technical and vocational skills, for employment, decent jobs and entrepreneurship', '2025-08-01 08:58:12.058256+00', '2025-08-01 08:58:12.058256+00'),
('803e5025-7385-4ade-bf28-635cea0d6205', 4, '4.5', 'Eliminate gender disparities', 'By 2030, eliminate gender disparities in education and ensure equal access to all levels of education and vocational training for the vulnerable, including persons with disabilities, indigenous peoples and children in vulnerable situations', '2025-08-01 08:58:12.091291+00', '2025-08-01 08:58:12.091291+00'),
('15928edf-c32a-4c51-9cdc-beea0cd8f2cd', 4, '4.6', 'Adult literacy and numeracy', 'By 2030, ensure that all youth and a substantial proportion of adults, both men and women, achieve literacy and numeracy', '2025-08-01 08:58:12.124196+00', '2025-08-01 08:58:12.124196+00'),
('66fb0dc8-2da1-4040-a0d9-2797f00cc720', 4, '4.7', 'Education for sustainable development', 'By 2030, ensure that all learners acquire the knowledge and skills needed to promote sustainable development, including, among others, through education for sustainable development and sustainable lifestyles, human rights, gender equality, promotion of a culture of peace and non-violence, global citizenship and appreciation of cultural diversity', '2025-08-01 08:58:12.162689+00', '2025-08-01 08:58:12.162689+00'),
('95ad8639-8623-47dc-95f9-81542ac69421', 4, '4.a', 'Build and upgrade education facilities', 'Build and upgrade education facilities that are child, disability and gender sensitive and provide safe, non-violent, inclusive and effective learning environments for all', '2025-08-01 08:58:12.195428+00', '2025-08-01 08:58:12.195428+00'),
('368961a8-fc62-4e5f-8f8d-38378cfa2a47', 4, '4.b', 'Expand higher education scholarships', 'By 2020, substantially expand globally the number of scholarships available to developing countries, in particular least developed countries, small island developing States and African countries, for enrolment in higher education', '2025-08-01 08:58:12.228595+00', '2025-08-01 08:58:12.228595+00'),
('2cd818a9-0185-4bb7-b6c9-c34778c21b22', 4, '4.c', 'Increase supply of qualified teachers', 'By 2030, substantially increase the supply of qualified teachers, including through international cooperation for teacher training in developing countries, especially least developed countries and small island developing States', '2025-08-01 08:58:12.261779+00', '2025-08-01 08:58:12.261779+00'),
('9f1b6c19-c14c-42f1-b326-602f47c6e280', 5, '5.1', 'End discrimination against women', 'End all forms of discrimination against all women and girls everywhere', '2025-08-01 08:58:12.294505+00', '2025-08-01 08:58:12.294505+00'),
('a7554a52-ac62-4d11-a75f-bbe13298ff28', 5, '5.2', 'Eliminate violence against women', 'Eliminate all forms of violence against all women and girls in the public and private spheres, including trafficking and sexual and other types of exploitation', '2025-08-01 08:58:12.326062+00', '2025-08-01 08:58:12.326062+00'),
('2605a252-7ecb-40a8-923b-0ff2f31495af', 5, '5.3', 'Eliminate harmful practices', 'Eliminate all harmful practices, such as child, early and forced marriage and female genital mutilation', '2025-08-01 08:58:12.358797+00', '2025-08-01 08:58:12.358797+00'),
('b6f8b7a0-8f0a-4fc3-9207-ab38f28a4f22', 5, '5.4', 'Value unpaid care work', 'Recognize and value unpaid care and domestic work through the provision of public services, infrastructure and social protection policies and the promotion of shared responsibility within the household and the family as nationally appropriate', '2025-08-01 08:58:12.391566+00', '2025-08-01 08:58:12.391566+00'),
('8f833934-f4d2-4eb5-aaba-d5ac08f315c3', 5, '5.5', 'Ensure full participation in leadership', 'Ensure women''s full and effective participation and equal opportunities for leadership at all levels of decision-making in political, economic and public life', '2025-08-01 08:58:12.424602+00', '2025-08-01 08:58:12.424602+00'),
('37c9fa19-1349-4148-a062-ee3b4bcb9968', 5, '5.6', 'Universal access to reproductive rights', 'Ensure universal access to sexual and reproductive health and reproductive rights as agreed in accordance with the Programme of Action of the International Conference on Population and Development and the Beijing Platform for Action', '2025-08-01 08:58:12.457602+00', '2025-08-01 08:58:12.457602+00'),
('ed246d1e-c25b-4c56-ac5f-74bfe3e378bb', 5, '5.a', 'Equal rights to economic resources', 'Undertake reforms to give women equal rights to economic resources, as well as access to ownership and control over land and other forms of property, financial services, inheritance and natural resources, in accordance with national laws', '2025-08-01 08:58:12.493777+00', '2025-08-01 08:58:12.493777+00'),
('02f7b73d-d77a-4538-8795-dc49ff8fcca0', 5, '5.b', 'Enhance use of enabling technology', 'Enhance the use of enabling technology, in particular information and communications technology, to promote the empowerment of women', '2025-08-01 08:58:12.52679+00', '2025-08-01 08:58:12.52679+00'),
('e358e599-242c-4b4d-80ba-b80327ee6420', 5, '5.c', 'Adopt policies for gender equality', 'Adopt and strengthen sound policies and enforceable legislation for the promotion of gender equality and the empowerment of all women and girls at all levels', '2025-08-01 08:58:12.559798+00', '2025-08-01 08:58:12.559798+00'),
('ef6b7bfb-0f9a-4051-817b-288a84b45b8e', 6, '6.1', 'Safe and affordable drinking water', 'By 2030, achieve universal and equitable access to safe and affordable drinking water for all', '2025-08-01 08:58:12.592612+00', '2025-08-01 08:58:12.592612+00'),
('18ea67ef-0b49-4a69-906d-30938bcd7727', 3, '3.4', 'Target 3.4', 'Target 3.4 description', '2025-08-01 10:03:15.268164+00', '2025-08-01 10:03:15.268164+00'),
('951a32dd-d0c7-4991-92ae-1f3b39ccdeb6', 3, '3.5', 'Target 3.5', 'Target 3.5 description', '2025-08-01 10:03:15.337488+00', '2025-08-01 10:03:15.337488+00'),
('f39ba782-b3e0-4203-b03c-7f6b0a3f4aad', 6, '6.2', 'Adequate sanitation and hygiene', 'By 2030, achieve access to adequate and equitable sanitation and hygiene for all and end open defecation, paying special attention to the needs of women and girls and those in vulnerable situations', '2025-08-01 08:58:12.625531+00', '2025-08-01 08:58:12.625531+00'),
('310c018f-b19f-406e-b4d2-f2bce8d05e44', 6, '6.3', 'Improve water quality', 'By 2030, improve water quality by reducing pollution, eliminating dumping and minimizing release of hazardous chemicals and materials, halving the proportion of untreated wastewater and substantially increasing recycling and safe reuse globally', '2025-08-01 08:58:12.65875+00', '2025-08-01 08:58:12.65875+00'),
('c1d82edc-4a19-4901-8163-5d0979057031', 6, '6.4', 'Increase water-use efficiency', 'By 2030, substantially increase water-use efficiency across all sectors and ensure sustainable withdrawals and supply of freshwater to address water scarcity and substantially reduce the number of people suffering from water scarcity', '2025-08-01 08:58:12.691874+00', '2025-08-01 08:58:12.691874+00'),
('7e4dad6b-93e0-48c5-96ed-d9190f1bba23', 6, '6.5', 'Integrated water resources management', 'By 2030, implement integrated water resources management at all levels, including through transboundary cooperation as appropriate', '2025-08-01 08:58:12.724583+00', '2025-08-01 08:58:12.724583+00'),
('11636864-3b99-43eb-abbe-9961002aee1d', 6, '6.6', 'Protect water-related ecosystems', 'By 2020, protect and restore water-related ecosystems, including mountains, forests, wetlands, rivers, aquifers and lakes', '2025-08-01 08:58:12.757694+00', '2025-08-01 08:58:12.757694+00'),
('f5a23176-f70e-4551-852e-f30e1d10d27e', 6, '6.a', 'Expand water cooperation', 'By 2030, expand international cooperation and capacity-building support to developing countries in water- and sanitation-related activities and programmes, including water harvesting, desalination, water efficiency, wastewater treatment, recycling and reuse technologies', '2025-08-01 08:58:12.794477+00', '2025-08-01 08:58:12.794477+00'),
('7cf6d7cb-e321-473b-9e56-57c57aed26c4', 6, '6.b', 'Support local participation', 'Support and strengthen the participation of local communities in improving water and sanitation management', '2025-08-01 08:58:12.827363+00', '2025-08-01 08:58:12.827363+00'),
('24762422-7282-4cd6-8018-b3d95e812cb5', 7, '7.1', 'Universal access to modern energy', 'By 2030, ensure universal access to affordable, reliable and modern energy services', '2025-08-01 08:58:12.861082+00', '2025-08-01 08:58:12.861082+00'),
('e38ce731-135d-4463-8e32-4b0a2e78d23c', 7, '7.2', 'Increase renewable energy', 'By 2030, increase substantially the share of renewable energy in the global energy mix', '2025-08-01 08:58:12.893029+00', '2025-08-01 08:58:12.893029+00'),
('15662f78-97bb-47df-87b1-d7804660711b', 7, '7.3', 'Double energy efficiency', 'By 2030, double the global rate of improvement in energy efficiency', '2025-08-01 08:58:12.925851+00', '2025-08-01 08:58:12.925851+00'),
('6dc386e1-6b6f-4136-b256-df752f4bafe9', 7, '7.a', 'Enhance energy cooperation', 'By 2030, enhance international cooperation to facilitate access to clean energy research and technology, including renewable energy, energy efficiency and advanced and cleaner fossil-fuel technology, and promote investment in energy infrastructure and clean energy technology', '2025-08-01 08:58:12.95801+00', '2025-08-01 08:58:12.95801+00'),
('ba0c2780-0cbc-4589-99e9-c86f85e8f670', 7, '7.b', 'Expand energy infrastructure', 'By 2030, expand infrastructure and upgrade technology for supplying modern and sustainable energy services for all in developing countries, in particular least developed countries, small island developing States, and land-locked developing countries, in accordance with their respective programmes of support', '2025-08-01 08:58:12.990182+00', '2025-08-01 08:58:12.990182+00'),
('ce22124b-d754-4e76-888f-216fec3930b0', 8, '8.1', 'Sustain economic growth', 'Sustain per capita economic growth in accordance with national circumstances and, in particular, at least 7 per cent gross domestic product growth per annum in the least developed countries', '2025-08-01 08:58:13.022178+00', '2025-08-01 08:58:13.022178+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('aa458a02-aed7-46c9-a275-33c59e610d59', 8, '8.2', 'Economic productivity through diversification', 'Achieve higher levels of economic productivity through diversification, technological upgrading and innovation, including through a focus on high-value added and labour-intensive sectors', '2025-08-01 08:58:13.054816+00', '2025-08-01 08:58:13.054816+00'),
('19cb03e2-aed2-43d7-bc5a-cbfd91ad512c', 8, '8.3', 'Promote development-oriented policies', 'Promote development-oriented policies that support productive activities, decent job creation, entrepreneurship, creativity and innovation, and encourage the formalization and growth of micro-, small- and medium-sized enterprises, including through access to financial services', '2025-08-01 08:58:13.087368+00', '2025-08-01 08:58:13.087368+00'),
('19e4f34a-224a-4ea2-9a86-7702e190bc4f', 8, '8.4', 'Improve resource efficiency', 'Improve progressively, through 2030, global resource efficiency in consumption and production and endeavour to decouple economic growth from environmental degradation, in accordance with the 10-year framework of programmes on sustainable consumption and production, with developed countries taking the lead', '2025-08-01 08:58:13.120281+00', '2025-08-01 08:58:13.120281+00'),
('2901a877-ba77-4714-afa0-86acf858b713', 8, '8.5', 'Full employment and decent work', 'By 2030, achieve full and productive employment and decent work for all women and men, including for young people and persons with disabilities, and equal pay for work of equal value', '2025-08-01 08:58:13.153093+00', '2025-08-01 08:58:13.153093+00'),
('28d00efd-572a-49ee-9884-ad9ab1b27557', 8, '8.6', 'Reduce youth NEET', 'By 2020, substantially reduce the proportion of youth not in employment, education or training', '2025-08-01 08:58:13.186202+00', '2025-08-01 08:58:13.186202+00'),
('2c21c188-3c60-4829-b86d-7e74146950e2', 8, '8.7', 'Eradicate forced labour', 'Take immediate and effective measures to eradicate forced labour, end modern slavery and human trafficking and secure the prohibition and elimination of the worst forms of child labour, including recruitment and use of child soldiers, and by 2025 end child labour in all its forms', '2025-08-01 08:58:13.219245+00', '2025-08-01 08:58:13.219245+00'),
('b7543a4e-3b4d-4124-82a7-a754facd7d3c', 8, '8.8', 'Protect labour rights', 'Protect labour rights and promote safe and secure working environments for all workers, including migrant workers, in particular women migrants, and those in precarious employment', '2025-08-01 08:58:13.251909+00', '2025-08-01 08:58:13.251909+00'),
('70cc0a63-6716-4433-8415-017900a50bb9', 8, '8.9', 'Promote sustainable tourism', 'By 2030, devise and implement policies to promote sustainable tourism that creates jobs and promotes local culture and products', '2025-08-01 08:58:13.284546+00', '2025-08-01 08:58:13.284546+00'),
('5626de3b-b008-4c3d-9ed1-e244c18580ef', 8, '8.10', 'Strengthen financial institutions', 'Strengthen the capacity of domestic financial institutions to encourage and expand access to banking, insurance and financial services for all', '2025-08-01 08:58:13.317287+00', '2025-08-01 08:58:13.317287+00'),
('6d0c93c0-3c92-4862-953f-782186c141c9', 8, '8.a', 'Increase Aid for Trade', 'Increase Aid for Trade support for developing countries, in particular least developed countries, including through the Enhanced Integrated Framework for Trade-Related Technical Assistance to Least Developed Countries', '2025-08-01 08:58:13.357974+00', '2025-08-01 08:58:13.357974+00'),
('246c67a5-4546-4ce9-8427-bdf36bee6408', 8, '8.b', 'Youth employment strategy', 'By 2020, develop and operationalize a global strategy for youth employment and implement the Global Jobs Pact of the International Labour Organization', '2025-08-01 08:58:13.390676+00', '2025-08-01 08:58:13.390676+00'),
('0a00b9c1-8826-4498-abae-b05eca6fdbe2', 9, '9.1', 'Develop resilient infrastructure', 'Develop quality, reliable, sustainable and resilient infrastructure, including regional and transborder infrastructure, to support economic development and human well-being, with a focus on affordable and equitable access for all', '2025-08-01 08:58:13.424447+00', '2025-08-01 08:58:13.424447+00'),
('e21ff682-8bd0-42f5-9ac5-25263ef4d0c4', 9, '9.2', 'Promote inclusive industrialization', 'Promote inclusive and sustainable industrialization and, by 2030, significantly raise industry''s share of employment and gross domestic product, in line with national circumstances, and double its share in least developed countries', '2025-08-01 08:58:13.45729+00', '2025-08-01 08:58:13.45729+00'),
('33fe115b-d61b-496a-8441-2347fd946d34', 9, '9.3', 'Increase access to financial services', 'Increase the access of small-scale industrial and other enterprises, in particular in developing countries, to financial services, including affordable credit, and their integration into value chains and markets', '2025-08-01 08:58:13.488947+00', '2025-08-01 08:58:13.488947+00'),
('cb029671-ed05-41ce-88fa-b910d93d8e19', 3, '3.4', 'Target 3.4', 'Target 3.4 description', '2025-08-01 10:03:15.303316+00', '2025-08-01 10:03:15.303316+00'),
('77085ef1-4605-406d-aa81-f44508810a8f', 9, '9.4', 'Upgrade infrastructure for sustainability', 'By 2030, upgrade infrastructure and retrofit industries to make them sustainable, with increased resource-use efficiency and greater adoption of clean and environmentally sound technologies and industrial processes, with all countries taking action in accordance with their respective capabilities', '2025-08-01 08:58:13.521505+00', '2025-08-01 08:58:13.521505+00'),
('29ddb517-e292-4947-bf27-2aff51a790eb', 9, '9.5', 'Enhance scientific research', 'Enhance scientific research, upgrade the technological capabilities of industrial sectors in all countries, in particular developing countries, including, by 2030, encouraging innovation and substantially increasing the number of research and development workers per 1 million people and public and private research and development spending', '2025-08-01 08:58:13.554439+00', '2025-08-01 08:58:13.554439+00'),
('00e1d524-33e8-471f-8bd4-5e9f49c75dd6', 9, '9.a', 'Facilitate sustainable infrastructure', 'Facilitate sustainable and resilient infrastructure development in developing countries through enhanced financial, technological and technical support to African countries, least developed countries, landlocked developing countries and small island developing States', '2025-08-01 08:58:13.587263+00', '2025-08-01 08:58:13.587263+00'),
('2a40a83c-d789-4ace-a779-a5c7bf4dcdd2', 9, '9.b', 'Support technology development', 'Support domestic technology development, research and innovation in developing countries, including by ensuring a conducive policy environment for, inter alia, industrial diversification and value addition to commodities', '2025-08-01 08:58:13.619698+00', '2025-08-01 08:58:13.619698+00'),
('685cd2b8-0124-4a5d-a595-a34adfc25a8b', 9, '9.c', 'Increase access to ICT', 'Significantly increase access to information and communications technology and strive to provide universal and affordable access to the Internet in least developed countries by 2020', '2025-08-01 08:58:13.652293+00', '2025-08-01 08:58:13.652293+00'),
('d7f44ada-f51d-4fd3-a6b4-d2b6227d0b65', 10, '10.1', 'Reduce income inequalities', 'By 2030, progressively achieve and sustain income growth of the bottom 40 per cent of the population at a rate higher than the national average', '2025-08-01 08:58:13.685788+00', '2025-08-01 08:58:13.685788+00'),
('d164cd85-e23b-413d-9bfc-495c53640974', 10, '10.2', 'Promote universal social inclusion', 'By 2030, empower and promote the social, economic and political inclusion of all, irrespective of age, sex, disability, race, ethnicity, origin, religion or economic or other status', '2025-08-01 08:58:13.718336+00', '2025-08-01 08:58:13.718336+00'),
('2f832bf7-d5e2-4943-a2a5-1b5715e0f2ee', 10, '10.3', 'Ensure equal opportunity', 'Ensure equal opportunity and reduce inequalities of outcome, including by eliminating discriminatory laws, policies and practices and promoting appropriate legislation, policies and action in this regard', '2025-08-01 08:58:13.750844+00', '2025-08-01 08:58:13.750844+00'),
('9fe9a7f4-ed64-4095-afd0-b9cc0a7a8707', 10, '10.4', 'Adopt fiscal and social policies', 'Adopt policies, especially fiscal, wage and social protection policies, and progressively achieve greater equality', '2025-08-01 08:58:13.783354+00', '2025-08-01 08:58:13.783354+00'),
('209e09a7-a5c7-404d-80d9-2d7a51f571fd', 10, '10.5', 'Improve regulation of financial markets', 'Improve the regulation and monitoring of global financial markets and institutions and strengthen the implementation of such regulations', '2025-08-01 08:58:13.815855+00', '2025-08-01 08:58:13.815855+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('d0106e55-4b42-4f94-bcc0-dacd3868f17f', 10, '10.6', 'Enhanced representation for developing countries', 'Ensure enhanced representation and voice for developing countries in decision-making in global international economic and financial institutions in order to deliver more effective, credible, accountable and legitimate institutions', '2025-08-01 08:58:13.848447+00', '2025-08-01 08:58:13.848447+00'),
('27c674fd-e445-4e48-8124-b0d09748be61', 10, '10.7', 'Facilitate safe migration', 'Facilitate orderly, safe, regular and responsible migration and mobility of people, including through the implementation of planned and well-managed migration policies', '2025-08-01 08:58:13.881172+00', '2025-08-01 08:58:13.881172+00'),
('68dfecf7-95d4-42c7-a1fa-c6090d0e7894', 10, '10.a', 'Special treatment for least developed countries', 'Implement the principle of special and differential treatment for developing countries, in particular least developed countries, in accordance with World Trade Organization agreements', '2025-08-01 08:58:13.914024+00', '2025-08-01 08:58:13.914024+00'),
('441a9990-10a8-4aa1-817a-f47dda0d6196', 10, '10.b', 'Encourage development assistance', 'Encourage official development assistance and financial flows, including foreign direct investment, to States where the need is greatest, in particular least developed countries, African countries, small island developing States and landlocked developing countries, in accordance with their national plans and programmes', '2025-08-01 08:58:13.946969+00', '2025-08-01 08:58:13.946969+00'),
('5251caf1-b107-4fc8-80d1-c82b046d068f', 10, '10.c', 'Reduce transaction costs of remittances', 'By 2030, reduce to less than 3 per cent the transaction costs of migrant remittances and eliminate remittance corridors with costs higher than 5 per cent', '2025-08-01 08:58:13.982564+00', '2025-08-01 08:58:13.982564+00'),
('a73b3224-cbac-4c7e-b75d-6af67ce98eca', 11, '11.1', 'Safe and affordable housing', 'By 2030, ensure access for all to adequate, safe and affordable housing and basic services and upgrade slums', '2025-08-01 08:58:14.015409+00', '2025-08-01 08:58:14.015409+00'),
('a5d70b5f-138b-49a7-a20d-9848e7c3aa8f', 11, '11.2', 'Sustainable transport systems', 'By 2030, provide access to safe, affordable, accessible and sustainable transport systems for all, improving road safety, notably by expanding public transport, with special attention to the needs of those in vulnerable situations, women, children, persons with disabilities and older persons', '2025-08-01 08:58:14.047096+00', '2025-08-01 08:58:14.047096+00'),
('f275d4c8-6d7d-4046-8b7b-b5ebf8cc24d9', 11, '11.3', 'Inclusive urbanization', 'By 2030, enhance inclusive and sustainable urbanization and capacity for participatory, integrated and sustainable human settlement planning and management in all countries', '2025-08-01 08:58:14.079591+00', '2025-08-01 08:58:14.079591+00'),
('006fdbb2-4c24-4bc0-bf59-ae8f3da426b6', 11, '11.4', 'Protect cultural heritage', 'Strengthen efforts to protect and safeguard the world''s cultural and natural heritage', '2025-08-01 08:58:14.111059+00', '2025-08-01 08:58:14.111059+00'),
('74f8d7ec-7c52-4477-affb-ca30a6c484c7', 11, '11.5', 'Reduce disaster losses', 'By 2030, significantly reduce the number of deaths and the number of people affected and substantially decrease the direct economic losses relative to global gross domestic product caused by disasters, including water-related disasters, with a focus on protecting the poor and people in vulnerable situations', '2025-08-01 08:58:14.144004+00', '2025-08-01 08:58:14.144004+00'),
('41bfe5f6-f0e6-4ee5-9bff-c5e215642ade', 11, '11.6', 'Reduce environmental impact of cities', 'By 2030, reduce the adverse per capita environmental impact of cities, including by paying special attention to air quality and municipal and other waste management', '2025-08-01 08:58:14.176911+00', '2025-08-01 08:58:14.176911+00'),
('88ee3f87-41fc-46ac-bd33-9fc84d2d6d33', 11, '11.7', 'Provide access to safe spaces', 'By 2030, provide universal access to safe, inclusive and accessible, green and public spaces, in particular for women and children, older persons and persons with disabilities', '2025-08-01 08:58:14.20996+00', '2025-08-01 08:58:14.20996+00'),
('5f29123c-be70-49ad-bcae-8b8d318108c8', 11, '11.a', 'Support positive urban-rural linkages', 'Support positive economic, social and environmental links between urban, peri-urban and rural areas by strengthening national and regional development planning', '2025-08-01 08:58:14.24238+00', '2025-08-01 08:58:14.24238+00'),
('b4ee003f-798f-4629-9c40-f04dc01985c5', 11, '11.b', 'Implement integrated policies', 'By 2020, substantially increase the number of cities and human settlements adopting and implementing integrated policies and plans towards inclusion, resource efficiency, mitigation and adaptation to climate change, resilience to disasters, and develop and implement, in line with the Sendai Framework for Disaster Risk Reduction 2015-2030, holistic disaster risk management at all levels', '2025-08-01 08:58:14.274099+00', '2025-08-01 08:58:14.274099+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('6ec552b7-359c-4af0-9add-7a60a739929f', 11, '11.c', 'Support least developed countries', 'Support least developed countries, including through financial and technical assistance, in building sustainable and resilient buildings utilizing local materials', '2025-08-01 08:58:14.306911+00', '2025-08-01 08:58:14.306911+00'),
('9500574b-2162-42e4-a683-9e4cf995256d', 3, '3.5', 'Target 3.5', 'Target 3.5 description', '2025-08-01 10:03:15.372357+00', '2025-08-01 10:03:15.372357+00'),
('c738506d-395a-40d1-8d64-c0bf6194d516', 3, '3.7', 'Target 3.7', 'Target 3.7 description', '2025-08-01 10:03:15.442097+00', '2025-08-01 10:03:15.442097+00'),
('e8b77639-ec2d-40ff-91fb-ce364658747a', 12, '12.1', 'Implement sustainable consumption patterns', 'Implement the 10-year framework of programmes on sustainable consumption and production, all countries taking action, with developed countries taking the lead, taking into account the development and capabilities of developing countries', '2025-08-01 08:58:14.339656+00', '2025-08-01 08:58:14.339656+00'),
('f01057e6-3f54-49ef-9926-9d27618813b1', 12, '12.2', 'Sustainable management of natural resources', 'By 2030, achieve the sustainable management and efficient use of natural resources', '2025-08-01 08:58:14.380865+00', '2025-08-01 08:58:14.380865+00'),
('a6479306-1794-419c-ad95-ece998bcb046', 12, '12.3', 'Halve food waste', 'By 2030, halve per capita global food waste at the retail and consumer levels and reduce food losses along production and supply chains, including post-harvest losses', '2025-08-01 08:58:14.413985+00', '2025-08-01 08:58:14.413985+00'),
('33bcf801-2e88-418a-a0a7-84051254906d', 12, '12.4', 'Responsible management of chemicals', 'By 2020, achieve the environmentally sound management of chemicals and all wastes throughout their life cycle, in accordance with agreed international frameworks, and significantly reduce their release to air, water and soil in order to minimize their adverse impacts on human health and the environment', '2025-08-01 08:58:14.446665+00', '2025-08-01 08:58:14.446665+00'),
('10c69815-89e8-4b02-af4a-ddc5d7dc90bc', 12, '12.5', 'Reduce waste generation', 'By 2030, substantially reduce waste generation through prevention, reduction, recycling and reuse', '2025-08-01 08:58:14.479247+00', '2025-08-01 08:58:14.479247+00'),
('ad150f3f-af73-4239-93de-0124490dc662', 12, '12.6', 'Encourage sustainable practices', 'Encourage companies, especially large and transnational companies, to adopt sustainable practices and to integrate sustainability information into their reporting cycle', '2025-08-01 08:58:14.511813+00', '2025-08-01 08:58:14.511813+00'),
('333f1de5-64a2-4aab-999f-0a2ad385894a', 12, '12.7', 'Promote sustainable procurement', 'Promote public procurement practices that are sustainable, in accordance with national policies and priorities', '2025-08-01 08:58:14.545605+00', '2025-08-01 08:58:14.545605+00'),
('c7a9b6f9-91c1-4ebf-92ae-0adf60d52b47', 12, '12.8', 'Promote awareness of sustainable development', 'By 2030, ensure that people everywhere have the relevant information and awareness for sustainable development and lifestyles in harmony with nature', '2025-08-01 08:58:14.578365+00', '2025-08-01 08:58:14.578365+00'),
('2fc42917-b032-4eeb-8880-9f31e8884dd5', 12, '12.a', 'Support developing countries'' scientific capacity', 'Support developing countries to strengthen their scientific and technological capacity to move towards more sustainable patterns of consumption and production', '2025-08-01 08:58:14.610749+00', '2025-08-01 08:58:14.610749+00'),
('3ad002f5-0c29-4c59-9f6f-89446a92c0dc', 12, '12.b', 'Develop sustainable tourism tools', 'Develop and implement tools to monitor sustainable development impacts for sustainable tourism that creates jobs and promotes local culture and products', '2025-08-01 08:58:14.643156+00', '2025-08-01 08:58:14.643156+00'),
('516a3ef8-14ce-47c8-9e2c-adc5ad0d5596', 12, '12.c', 'Remove market distortions in fossil fuels', 'Rationalize inefficient fossil-fuel subsidies that encourage wasteful consumption by removing market distortions, in accordance with national circumstances, including by restructuring taxation and phasing out those harmful subsidies, where they exist, to reflect their environmental impacts, taking fully into account the specific needs and conditions of developing countries and minimizing the possible adverse impacts on their development in a manner that protects the poor and the affected communities', '2025-08-01 08:58:14.675736+00', '2025-08-01 08:58:14.675736+00'),
('3b22f5ca-5b40-411e-9cdb-657c8da290a9', 13, '13.1', 'Strengthen resilience to climate hazards', 'Strengthen resilience and adaptive capacity to climate-related hazards and natural disasters in all countries', '2025-08-01 08:58:14.708521+00', '2025-08-01 08:58:14.708521+00'),
('6a1124be-671d-468e-854c-cc3d6f95d65e', 13, '13.2', 'Integrate climate measures', 'Integrate climate change measures into national policies, strategies and planning', '2025-08-01 08:58:14.74004+00', '2025-08-01 08:58:14.74004+00'),
('111b3a38-7511-4342-a447-fb6452fad0f8', 13, '13.3', 'Improve education on climate change', 'Improve education, awareness-raising and human and institutional capacity on climate change mitigation, adaptation, impact reduction and early warning', '2025-08-01 08:58:14.772533+00', '2025-08-01 08:58:14.772533+00'),
('442c8c73-a73d-48d2-8a92-0e1760a35926', 13, '13.a', 'Implement climate finance commitment', 'Implement the commitment undertaken by developed-country parties to the United Nations Framework Convention on Climate Change to a goal of mobilizing jointly $100 billion annually by 2020 from all sources to address the needs of developing countries in the context of meaningful mitigation actions and transparency on implementation and fully operationalize the Green Climate Fund through its capitalization as soon as possible', '2025-08-01 08:58:14.803847+00', '2025-08-01 08:58:14.803847+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('df89ba27-001c-4203-b618-1100b974d958', 13, '13.b', 'Promote mechanisms for climate planning', 'Promote mechanisms for raising capacity for effective climate change-related planning and management in least developed countries and small island developing States, including focusing on women, youth and local and marginalized communities', '2025-08-01 08:58:14.836522+00', '2025-08-01 08:58:14.836522+00'),
('a2e31ba5-f849-42ce-878f-073dcda3670c', 14, '14.1', 'Reduce marine pollution', 'By 2025, prevent and significantly reduce marine pollution of all kinds, in particular from land-based activities, including marine debris and nutrient pollution', '2025-08-01 08:58:14.869181+00', '2025-08-01 08:58:14.869181+00'),
('4cb4358b-a013-4500-8354-0a6949a345d5', 14, '14.2', 'Protect marine ecosystems', 'By 2020, sustainably manage and protect marine and coastal ecosystems to avoid significant adverse impacts, including by strengthening their resilience, and take action for their restoration in order to achieve healthy and productive oceans', '2025-08-01 08:58:14.901661+00', '2025-08-01 08:58:14.901661+00'),
('16111a79-3a67-4d81-98f7-976547788b4d', 14, '14.3', 'Minimize ocean acidification', 'Minimize and address the impacts of ocean acidification, including through enhanced scientific cooperation at all levels', '2025-08-01 08:58:14.934307+00', '2025-08-01 08:58:14.934307+00'),
('126bf7c1-c432-46f0-a552-96b720659cd6', 14, '14.4', 'Regulate fishing', 'By 2020, effectively regulate harvesting and end overfishing, illegal, unreported and unregulated fishing and destructive fishing practices and implement science-based management plans, in order to restore fish stocks in the shortest time feasible, at least to levels that can produce maximum sustainable yield as determined by their biological characteristics', '2025-08-01 08:58:14.965965+00', '2025-08-01 08:58:14.965965+00'),
('68fe0299-27c0-4e03-b1d2-efb239811ca9', 14, '14.5', 'Conserve marine areas', 'By 2020, conserve at least 10 per cent of coastal and marine areas, consistent with national and international law and based on the best available scientific information', '2025-08-01 08:58:14.998961+00', '2025-08-01 08:58:14.998961+00'),
('dc189491-dc5e-4bb2-b463-e533bcb37b98', 14, '14.6', 'End subsidies contributing to overfishing', 'By 2020, prohibit certain forms of fisheries subsidies which contribute to overcapacity and overfishing, eliminate subsidies that contribute to illegal, unreported and unregulated fishing and refrain from introducing new such subsidies, recognizing that appropriate and effective special and differential treatment for developing and least developed countries should be an integral part of the World Trade Organization fisheries subsidies negotiation', '2025-08-01 08:58:15.032+00', '2025-08-01 08:58:15.032+00'),
('fdf226c6-9280-4655-916e-04cb016b1b9b', 14, '14.7', 'Increase economic benefits from marine resources', 'By 2030, increase the economic benefits to Small Island developing States and least developed countries from the sustainable use of marine resources, including through sustainable management of fisheries, aquaculture and tourism', '2025-08-01 08:58:15.064591+00', '2025-08-01 08:58:15.064591+00'),
('6a6c94ee-62f6-4113-966f-2f646a3d94a5', 14, '14.a', 'Increase scientific knowledge', 'Increase scientific knowledge, develop research capacity and transfer marine technology, taking into account the Intergovernmental Oceanographic Commission Criteria and Guidelines on the Transfer of Marine Technology, in order to improve ocean health and to enhance the contribution of marine biodiversity to the development of developing countries, in particular small island developing States and least developed countries', '2025-08-01 08:58:15.097445+00', '2025-08-01 08:58:15.097445+00'),
('3f7aa22e-d990-49d3-83f1-665fa6a95996', 14, '14.b', 'Provide access for small-scale fishers', 'Provide access for small-scale artisanal fishers to marine resources and markets', '2025-08-01 08:58:15.130012+00', '2025-08-01 08:58:15.130012+00'),
('d919466f-b19f-447f-882c-856725966da3', 14, '14.c', 'Enhance conservation through international law', 'Enhance the conservation and sustainable use of oceans and their resources by implementing international law as reflected in UNCLOS, which provides the legal framework for the conservation and sustainable use of oceans and their resources, as recalled in paragraph 158 of The Future We Want', '2025-08-01 08:58:15.162628+00', '2025-08-01 08:58:15.162628+00'),
('8d3beb70-7bf6-4ff9-b00e-66b82353a6b4', 15, '15.1', 'Conserve terrestrial ecosystems', 'By 2020, ensure the conservation, restoration and sustainable use of terrestrial and inland freshwater ecosystems and their services, in particular forests, wetlands, mountains and drylands, in line with obligations under international agreements', '2025-08-01 08:58:15.203022+00', '2025-08-01 08:58:15.203022+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('97f55e42-711d-4e33-9baf-7aee06faf3cd', 15, '15.2', 'End deforestation', 'By 2020, promote the implementation of sustainable management of all types of forests, halt deforestation, restore degraded forests and substantially increase afforestation and reforestation globally', '2025-08-01 08:58:15.235702+00', '2025-08-01 08:58:15.235702+00'),
('84e29c7b-7183-4975-852a-6c80e6018861', 15, '15.3', 'Combat desertification', 'By 2030, combat desertification, restore degraded land and soil, including land affected by desertification, drought and floods, and strive to achieve a land degradation-neutral world', '2025-08-01 08:58:15.269536+00', '2025-08-01 08:58:15.269536+00'),
('b567a4fd-c45e-4784-8afb-01a8ad09bea9', 15, '15.4', 'Conserve mountain ecosystems', 'By 2030, ensure the conservation of mountain ecosystems, including their biodiversity, in order to enhance their capacity to provide benefits that are essential for sustainable development', '2025-08-01 08:58:15.301892+00', '2025-08-01 08:58:15.301892+00'),
('6aa1c4cb-7027-41a1-a76a-50031267bf40', 15, '15.5', 'Protect biodiversity', 'Take urgent and significant action to reduce the degradation of natural habitats, halt the loss of biodiversity and, by 2020, protect and prevent the extinction of threatened species', '2025-08-01 08:58:15.334692+00', '2025-08-01 08:58:15.334692+00'),
('5d9abedd-f5fe-4580-a058-6251ea096def', 15, '15.6', 'Promote access to genetic resources', 'Promote fair and equitable sharing of the benefits arising from the utilization of genetic resources and promote appropriate access to such resources, as internationally agreed', '2025-08-01 08:58:15.367577+00', '2025-08-01 08:58:15.367577+00'),
('f3de7887-2fd3-4840-9b69-b72a9b537abe', 15, '15.7', 'End poaching and trafficking', 'Take urgent action to end poaching and trafficking of protected species of flora and fauna and address both demand and supply of illegal wildlife products', '2025-08-01 08:58:15.400379+00', '2025-08-01 08:58:15.400379+00'),
('d5d50cb8-2293-4606-9b9a-c5eceadb71a5', 15, '15.8', 'Prevent invasive species', 'By 2020, introduce measures to prevent the introduction and significantly reduce the impact of invasive alien species on land and water ecosystems and control or eradicate the priority species', '2025-08-01 08:58:15.431944+00', '2025-08-01 08:58:15.431944+00'),
('88df0838-2d9f-4e9a-9c0e-ed59c2a34dff', 15, '15.9', 'Integrate ecosystem values', 'By 2020, integrate ecosystem and biodiversity values into national and local planning, development processes, poverty reduction strategies and accounts', '2025-08-01 08:58:15.464756+00', '2025-08-01 08:58:15.464756+00'),
('0a73e4f3-f921-463c-969b-284d4c7d46a6', 15, '15.a', 'Mobilize resources for biodiversity', 'Mobilize and significantly increase financial resources from all sources to conserve and sustainably use biodiversity and ecosystems', '2025-08-01 08:58:15.497366+00', '2025-08-01 08:58:15.497366+00'),
('cfab1fb6-70a5-4ac2-aa2b-cdcbe0eaad89', 15, '15.b', 'Finance sustainable forest management', 'Mobilize significant resources from all sources and at all levels to finance sustainable forest management and provide adequate incentives to developing countries to advance such management, including for conservation and reforestation', '2025-08-01 08:58:15.529005+00', '2025-08-01 08:58:15.529005+00'),
('8759ffb3-b024-482a-bee2-039a8c7a0a30', 15, '15.c', 'Combat poaching and trafficking', 'Enhance global support for efforts to combat poaching and trafficking of protected species, including by increasing the capacity of local communities to pursue sustainable livelihood opportunities', '2025-08-01 08:58:15.56188+00', '2025-08-01 08:58:15.56188+00'),
('8f76f947-02e6-41f6-b1a6-b06d35197f56', 16, '16.1', 'Reduce violence', 'Significantly reduce all forms of violence and related death rates everywhere', '2025-08-01 08:58:15.59485+00', '2025-08-01 08:58:15.59485+00'),
('c338820a-9d61-44f9-80a9-075811ca78ba', 16, '16.2', 'End abuse and trafficking of children', 'End abuse, exploitation, trafficking and all forms of violence against and torture of children', '2025-08-01 08:58:15.627286+00', '2025-08-01 08:58:15.627286+00');
INSERT INTO "public"."sdg_targets" ("id", "sdg_goal_id", "target_number", "title", "description", "created_at", "updated_at") VALUES
('74021633-6ae9-4e59-89a9-c28b34c3e130', 16, '16.3', 'Promote rule of law', 'Promote the rule of law at the national and international levels and ensure equal access to justice for all', '2025-08-01 08:58:15.660177+00', '2025-08-01 08:58:15.660177+00'),
('ec3492c0-2ee8-44e7-9477-318a1683d882', 16, '16.4', 'Combat illicit financial flows', 'By 2030, significantly reduce illicit financial and arms flows, strengthen the recovery and return of stolen assets and combat all forms of organized crime', '2025-08-01 08:58:15.692564+00', '2025-08-01 08:58:15.692564+00'),
('cc5d6ca0-dde4-406b-8227-bb8480ccc216', 16, '16.5', 'Reduce corruption and bribery', 'Substantially reduce corruption and bribery in all their forms', '2025-08-01 08:58:15.725025+00', '2025-08-01 08:58:15.725025+00'),
('0b6c56a8-3c62-40fb-a9e3-416e8d3a9139', 16, '16.6', 'Develop effective institutions', 'Develop effective, accountable and transparent institutions at all levels', '2025-08-01 08:58:15.757712+00', '2025-08-01 08:58:15.757712+00'),
('8999b3f8-484a-4615-9e54-c92c800296a1', 16, '16.7', 'Ensure responsive decision-making', 'Ensure responsive, inclusive, participatory and representative decision-making at all levels', '2025-08-01 08:58:15.790176+00', '2025-08-01 08:58:15.790176+00'),
('06d8f255-3f6d-4054-b49e-c4de0ad880d3', 16, '16.8', 'Broaden participation in global governance', 'Broaden and strengthen the participation of developing countries in the institutions of global governance', '2025-08-01 08:58:15.82627+00', '2025-08-01 08:58:15.82627+00'),
('32d95a36-eb5a-45ac-86d9-4ce75a92aa6f', 16, '16.9', 'Legal identity for all', 'By 2030, provide legal identity for all, including birth registration', '2025-08-01 08:58:15.859356+00', '2025-08-01 08:58:15.859356+00'),
('dba99323-2d40-41af-a3bc-b3cb4abf729e', 16, '16.10', 'Ensure public access to information', 'Ensure public access to information and protect fundamental freedoms, in accordance with national legislation and international agreements', '2025-08-01 08:58:15.890965+00', '2025-08-01 08:58:15.890965+00'),
('5a079715-867c-4c7a-aded-24e152357b42', 16, '16.a', 'Strengthen institutions to prevent violence', 'Strengthen relevant national institutions, including through international cooperation, for building capacity at all levels, in particular in developing countries, to prevent violence and combat terrorism and crime', '2025-08-01 08:58:15.923581+00', '2025-08-01 08:58:15.923581+00'),
('9d40850c-20e7-43c7-999c-8a637e22ee7b', 16, '16.b', 'Promote non-discriminatory laws', 'Promote and enforce non-discriminatory laws and policies for sustainable development', '2025-08-01 08:58:15.955016+00', '2025-08-01 08:58:15.955016+00'),
('84c39da7-f6f8-4098-950d-1fd98e754713', 17, '17.1', 'Strengthen domestic revenue collection', 'Strengthen domestic resource mobilization, including through international support to developing countries, to improve domestic capacity for tax and other revenue collection', '2025-08-01 08:58:15.987443+00', '2025-08-01 08:58:15.987443+00'),
('46f699aa-a755-4508-8b62-26be5d03d552', 17, '17.2', 'Implement ODA commitments', 'Developed countries to implement fully their official development assistance commitments, including the commitment by many developed countries to achieve the target of 0.7 per cent of gross national income for official development assistance (ODA/GNI) to developing countries and 0.15 to 0.20 per cent of ODA/GNI to least developed countries', '2025-08-01 08:58:16.020604+00', '2025-08-01 08:58:16.020604+00'),
('695f657d-31b8-41fa-8ec5-3fc7162f8f39', 17, '17.3', 'Mobilize financial resources', 'Mobilize additional financial resources for developing countries from multiple sources', '2025-08-01 08:58:16.052043+00', '2025-08-01 08:58:16.052043+00'),
('bbffe146-2878-45f2-9b6a-68d40bb98d83', 17, '17.4', 'Assist developing countries in debt sustainability', 'Assist developing countries in attaining long-term debt sustainability through coordinated policies aimed at fostering debt financing, debt relief and debt restructuring, as appropriate, and address the external debt of highly indebted poor countries to reduce debt distress', '2025-08-01 08:58:16.08457+00', '2025-08-01 08:58:16.08457+00'),
('813b54d7-c9d1-4601-ae48-fb1de812a4ee', 17, '17.5', 'Adopt investment promotion regimes', 'Adopt and implement investment promotion regimes for least developed countries', '2025-08-01 08:58:16.117233+00', '2025-08-01 08:58:16.117233+00'),
('a2f97f6a-728e-45f7-aa28-c34f9b8de3b5', 3, '3.6', 'Target 3.6', 'Target 3.6 description', '2025-08-01 10:03:15.406653+00', '2025-08-01 10:03:15.406653+00'),
('7bb39529-7384-471b-959c-940068b0dfd2', 17, '17.6', 'Enhance cooperation on science and technology', 'Enhance North-South, South-South and triangular regional and international cooperation on and access to science, technology and innovation and enhance knowledge sharing on mutually agreed terms, including through improved coordination among existing mechanisms, in particular at the United Nations level, and through a global technology facilitation mechanism', '2025-08-01 08:58:16.149872+00', '2025-08-01 08:58:16.149872+00'),
('d7fa9b6c-25c1-419e-b6ae-516c539ebacd', 17, '17.7', 'Promote sustainable technologies', 'Promote the development, transfer, dissemination and diffusion of environmentally sound technologies to developing countries on favourable terms, including on concessional and preferential terms, as mutually agreed', '2025-08-01 08:58:16.183748+00', '2025-08-01 08:58:16.183748+00'),
('2766b11d-2f51-4542-9424-f7bd078443b1', 17, '17.8', 'Operationalize technology bank', 'Fully operationalize the technology bank and science, technology and innovation capacity-building mechanism for least developed countries by 2017 and enhance the use of enabling technology, in particular information and communications technology', '2025-08-01 08:58:16.216248+00', '2025-08-01 08:58:16.216248+00'),
('21b280b8-d65d-4d2b-8a04-ddf6544e3ea2', 17, '17.9', 'Enhance capacity-building support', 'Enhance international support for implementing effective and targeted capacity-building in developing countries to support national plans to implement all the sustainable development goals, including through North-South, South-South and triangular cooperation', '2025-08-01 08:58:16.248847+00', '2025-08-01 08:58:16.248847+00'),
('3ed8ca64-2094-412b-bb6a-1229a47a00d5', 17, '17.10', 'Promote universal trading system', 'Promote a universal, rules-based, open, non-discriminatory and equitable multilateral trading system under the World Trade Organization, including through the conclusion of negotiations under its Doha Development Agenda', '2025-08-01 08:58:16.281252+00', '2025-08-01 08:58:16.281252+00'),
('4fce00af-8e77-417b-9f00-0ed78e78a203', 17, '17.11', 'Increase developing country exports', 'Significantly increase the exports of developing countries, in particular with a view to doubling the least developed countries'' share of global exports by 2020', '2025-08-01 08:58:16.314118+00', '2025-08-01 08:58:16.314118+00'),
('43cc10b0-f2d9-45f8-9f8a-0dd91ff3e631', 17, '17.12', 'Realize timely duty-free access', 'Realize timely implementation of duty-free and quota-free market access on a lasting basis for all least developed countries, consistent with World Trade Organization decisions, including by ensuring that preferential rules of origin applicable to imports from least developed countries are transparent and simple, and contribute to facilitating market access', '2025-08-01 08:58:16.348369+00', '2025-08-01 08:58:16.348369+00'),
('16e4a98d-d6b8-4da2-8851-1afc2a74b1df', 17, '17.13', 'Enhance macroeconomic stability', 'Enhance global macroeconomic stability, including through policy coordination and policy coherence', '2025-08-01 08:58:16.380743+00', '2025-08-01 08:58:16.380743+00'),
('cb16a084-6004-43d5-81ae-939579f799c1', 17, '17.14', 'Enhance policy coherence', 'Enhance policy coherence for sustainable development', '2025-08-01 08:58:16.413142+00', '2025-08-01 08:58:16.413142+00'),
('74d7fc5c-672e-4bba-a405-f036876636b0', 17, '17.15', 'Respect policy space', 'Respect each country''s policy space and leadership to establish and implement policies for poverty eradication and sustainable development', '2025-08-01 08:58:16.446353+00', '2025-08-01 08:58:16.446353+00'),
('4000422b-32fd-4acf-8d9c-313a1e3888d4', 17, '17.16', 'Enhance global partnership', 'Enhance the global partnership for sustainable development, complemented by multi-stakeholder partnerships that mobilize and share knowledge, expertise, technology and financial resources, to support the achievement of the sustainable development goals in all countries, in particular developing countries', '2025-08-01 08:58:16.479719+00', '2025-08-01 08:58:16.479719+00'),
('e2d725c6-a781-4d9b-abbb-84e7e1f21887', 17, '17.17', 'Encourage effective partnerships', 'Encourage and promote effective public, public-private and civil society partnerships, building on the experience and resourcing strategies of partnerships', '2025-08-01 08:58:16.512373+00', '2025-08-01 08:58:16.512373+00'),
('c7f3c779-1d39-482e-bd58-491b9994cdc0', 17, '17.18', 'Enhance data availability', 'By 2020, enhance capacity-building support to developing countries, including for least developed countries and small island developing States, to increase significantly the availability of high-quality, timely and reliable data disaggregated by income, gender, age, race, ethnicity, migratory status, disability, geographic location and other characteristics relevant in national contexts', '2025-08-01 08:58:16.544764+00', '2025-08-01 08:58:16.544764+00'),
('90f7b009-071c-4c33-90f2-acbb3ef94ab9', 17, '17.19', 'Develop broader progress measures', 'By 2030, build on existing initiatives to develop measurements of progress on sustainable development that complement gross domestic product, and support statistical capacity-building in developing countries', '2025-08-01 08:58:16.577568+00', '2025-08-01 08:58:16.577568+00'),
